css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("lioden.com") {
body {
    background: url('http://orig10.deviantart.net/6034/f/2016/005/e/6/blue_wallpaper_by_roselynn1214-d9mu48f.png')!important;
    background-attachment: fixed!important;
    background-size: 100%!important;
}
h1,
h2,
h3 {
    color: black!important;
}
table.table td {
    background: #6497b1!important;
    color: black!important;
}
td.right,
.sub_menu,
.right_odd.xsmall,
.right_locked,
td.right_odd,
span.left,
.customiser-sortable li:nth-child(2n+1) {
    background: #6497b1!important;
    color: black!important;
}
th,
td.top,
td.bottom,
div.left {
    background: #005b96!important;
    color: black!important;
}
td.left.b.center,
td.left.b,
td.left,
td.right_cub,
.sub_menu_item_selected {
    background: #011f4b!important;
    color: black!important;
}
a:link,
a:visited {
    color: black!important;
}
a:hover,
a:active {
    color: white!important;
    transition: all ease 2s;
}
.sidebar,
.navbar {
    background: #005b96!important;
    color: black!important;
}
.navbar {
    border-top-right-radius: 15px;
}
.sidebar {
    border-bottom-right-radius: 15px;
    border-left: 1px solid black;
}
.navbar-default .navbar-nav > li > a {
    color: black!important;
}
.navbar-default .navbar-nav > li > a:hover {
    background: #b3cde0!important;
    transition: all ease 3s;
}
.xsmall {
    background: none!important;
}
.panel,
.sub_menu,
.sub_sub_menu,
.pagination span {
    box-shadow: none!important;
    border: 1px solid black!important;
    background: #011f4b!important;
    border-radius: 15px;
    color: white!important;
}
.chat_light {
    background: #6497b1!important;
    color: black!important;
}
.chat_dark {
    background: #011f4b!important;
    color: white!important;
}
.topbar,
.breadcrumb {
    background: #6497b1!important;
    color: black!important;
}
.breadcrumb > .active {
    color: white!important;
}
input[type="submit"],
input[type="button"] {
    color: #b3cde0!important;
    background: #011f4b!important;
    border: 2px solid black!important;
    box-shadow: none!important;
    text-shadow: none!important;
    transition: all ease 1s;
    border-radius: 15px!important;
}
input[type="submit"]:hover,
input[type="button"]:hover {
    color: #005b96!important;
    background: #011f4b!important;
    border: 2px solid black!important;
    box-shadow: none!important;
    text-shadow: none!important;
    border-radius: 15px!important;
}
.progress-bar-warning,
.progress-bar-danger,
.progress-bar-success,
.progress-bar {
    background: #6497b1!important;
}
.progress {
    background: #b3cde0!important;
    border: 1px solid black;
    border-radius: 15px;
}
.progress div {
    color: black;
}
div#lion_image>div {
    background: url('https://dl.dropbox.com/s/er1v1hqfe8b2w7c/Simple%20Blue%20Lion%20BG.png')!important;
}
table.table,
div.table {
    border: 1px solid black!important;
}
input[type="text"] {
    color: black;
}
input[type="password"] {
    color: black;
}
textarea {
    color: black;
}
select {
    color: black;
}
div.container.main {
    background: #b3cde0!important;
    box-shadow: none!important;
    border: 2px solid black!important;
    border-top-right-radius: 15px!important;
    border-bottom-right-radius: 15px!important;
    border-bottom-left-radius: 15px!important;
}
div.col-md-9.col-xs-12 {
    background: none!important;
    border: none!important;
    border-top: 2px solid black!important;
}
.item {
    border: none!important;
    background:#6497b1!important;
}
.item-footer {
    background: #005b96!important;
    color: black;
}
.item-header {
    background: #005b96!important;
    color: black;
}
.badge { 
  background:#005b96!important;
color:black!important;
}

}
