css:
@-moz-document domain("boards.4chan.org") {
* {
    border: none!important;
    background: none!important;
    font-family: segoe ui!important;
    overflow-x: hidden;
}
input,
textarea {
    background: #414141!important;
}
div.post.reply {
    width: 50%!important;
    margin-left: 2%!important;
    padding-top: 5px!important;
    padding-left: 5px!important;
}
.reply:target,
.reply.highlight {
    background-color: #eee!important;
    border: none!important;
}
.dd-menu li {
    padding: 2!important;
}
.burichan_new .backlink a:hover,
.yotsuba_b_new .backlink a:hover ~ div {
    display: none!important;
}
blockquote.postMessage {
    max-width: 900px!important;
    text-align: justify!important;
}
html {
    background: #212121!important;
    background-position: right bottom!important;
    background-attachment: fixed!important;
    background-repeat: no-repeat!important;
    background-size: 33%!important;
}
a>img {
    box-shadow: #414141 0 0 3px!important
}
form>table {
    padding: 4px !important;
}
.reply,
.replyhl {
    background: #282828!important;
    margin: 5px 0px!important;
    border-radius: 0px!important;
    box-shadow: 0 0 0px rgba(0, 0, 0, 0.2)!important;
}
blockquote,
.quotejs,
span.quote {
    color: #999!important;
}
a.fileThumb img {
    margin: 5px !important;
    border: none !important;
}
img#recaptcha_reload,
img#recaptcha_switch_audio,
img#recaptcha_whatsthis {
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.2)!important;
}
span.subject {
    color: #66FF99 !important;
}
span.postNum,
a,
span.fileText,
div.backlink,
span.spoiler {
    color: #ccc!important;
}
img.bottomad,
div#absbot,
div.center.middlead,
div.center,
hr,
div.sideArrows,
strong,
img#toggleMsgBtn,
img.middlead,
div#styleSwitcher,
div#boardNavDesktopFoot,
span.postertrip,
.click-me {
    display: none!important;
}
div.pagelist.desktop,
form {
    margin-bottom: 20px!important;
}
img.topad {
    width: 37%!important;
    height: 37%!important;
}
label,
li,
span.posteruid,
span.password,
div.passNotice,
td,
div#ctrl,
span.navLinks,
#search-label-bottom,
.button,
span.password,
div.passNotice,
td,
span.deadlink {
    color: grey!important;
}
a.quotelink:link,
a.quotelink:link:hover {
    color: #9b9b9b!important;
}
a.quotelink:visited,
a.quotelink:visited:hover {
    color: #282828!important;
}
span.posteruid {
    color: #66FF99
}
span.hand {
    color: #66FF99!important;
}
s {
    background-color: #282828!important;
}
s:hover {
    color: #66FF99!important;
}
span.hand:hover,
div.error,
S,
span.name,
.dateTime,
a.replylink:link,
a:hover {
    color: #66FF99!important;
}
div.fileText {
    color: #ddd!important;
}
div.navLinks.desktop {
    color: rgba(0, 0, 0, 1)!important;
}
span#togglePostFormLink.button {
    color: #282828!important;
}
.bottomCtrl.desktop {
    background: white!important;
    padding: 7px!important;
    border-radius: 5px!important;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.3)!important;
}
#quickReply {
    padding-left: 5px!important;
    padding-top: 5px!important;
    padding-right: 5px!important;
    padding-bottom: 0!important;
}
}
