css:
@-moz-document domain(trello.com)
{

/* latin-ext */
@font-face {
  font-family: 'Open Sans Condensed';
  font-style: normal;
  font-weight: 300;
  src: local('Open Sans Cond Light'), local('OpenSans-CondensedLight'), url(https://fonts.gstatic.com/s/opensanscondensed/v10/gk5FxslNkTTHtojXrkp-xC8hAQ4ocbp44gFQt8tMfcH3rGVtsTkPsbDajuO5ueQw.woff2) format('woff2');
}
/* latin */
@font-face {
  font-family: 'Open Sans Condensed';
  font-style: normal;
  font-weight: 300;
  src: local('Open Sans Cond Light'), local('OpenSans-CondensedLight'), url(https://fonts.gstatic.com/s/opensanscondensed/v10/gk5FxslNkTTHtojXrkp-xBEur64QvLD-0IbiAdTUNXE.woff2) format('woff2');
}

/* latin-ext */
@font-face {
  font-family: 'Open Sans Condensed';
  font-style: normal;
  font-weight: 700;
  src: local('Open Sans Condensed Bold'), local('OpenSans-CondensedBold'), url(https://fonts.gstatic.com/s/opensanscondensed/v10/gk5FxslNkTTHtojXrkp-xBztfhWvsElVqpbWxzFHK633rGVtsTkPsbDajuO5ueQw.woff2) format('woff2');
}
/* latin */
@font-face {
  font-family: 'Open Sans Condensed';
  font-style: normal;
  font-weight: 700;
  src: local('Open Sans Condensed Bold'), local('OpenSans-CondensedBold'), url(https://fonts.gstatic.com/s/opensanscondensed/v10/gk5FxslNkTTHtojXrkp-xIF8zhZCJ7x9Pr62cRgaVUA.woff2) format('woff2');
}

body {
    background-color: #222 !important;
    transition: opacity ease-in .25s;
}
body,
button,
html,
input,
select,
textarea {
  font-family: 'Open Sans Condensed' !important;
}
textarea.mod-list-name {
    font-size: 28px;
    color: Black;
    font-weight: bold;
    padding: 5px 3px;
    margin-bottom: 0px;
    height: 33px !important;
}
body #surface #header {
    max-height: 0px;
    opacity: 0;
    transition: opacity ease-in-out .5s, max-height ease-in-out 0.5s;
    transition-delay: 5s;
}
body #surface #header:hover {
    max-height: 30px;
    opacity: 1;
    transition-delay: 0s;
}
.board-header {
    padding: 4px;
    opacity: 0;
    max-height: 0px;
    transition: max-height ease-in-out .5s, opacity ease-in .5s;
    transition-delay: 5s;
}
.board-header:hover {
    opacity: 1;
    max-height: 35px;
    transition-delay: 0s;
}
.header-logo {
    margin-left: -250px;
    opacity: 1;
    transition: none;
}
.header-logo,
.header-logo-default {
    width: 500px;
}
.header-logo-default {
    opacity: .5;
    transition: opacity ease-in-out .5s;
    background-image: none;
    background-size: auto;
}
.header-logo:hover,
.header-logo-default:hover {
    opacity: 1;
}
body .list {
    background-color: #ddd;
}
body .list-card-labels {
    float: right;
    top: -4px;
    right: -8px;
    max-width: 200px;
    margin: 0;
    text-align: right;
}
body .list-card-labels .card-label {
    float: right;
    width: auto;
    height: 16px;
    line-height: 16px;
    padding: 1px 5px;
    margin: 0;
    font-size: 10px;
    font-style: normal;
    border-radius: 0 3px;
    overflow: visible;
}
body .list-card-details {
    padding: 4px 8px;
}
body .list-card-details .badges {
    padding-bottom: 0px;
}
body .list-header {
    padding: 8px 10px 3px 10px;
}
body .list-header textarea {
    font-size: 28px;
}
.board-canvas .js-list.list-wrapper:nth-child(1) textarea.list-header-name {
    color: #999;
}
.board-canvas .js-list.list-wrapper:nth-child(2) textarea.list-header-name {
    color: #f28e00;
}
.board-canvas .js-list.list-wrapper:nth-child(3) textarea.list-header-name {
    color: Red;
}
.board-canvas .js-list.list-wrapper:nth-child(4) textarea.list-header-name {
    color: Green;
}
.board-canvas .js-list.list-wrapper:nth-child(5) textarea.list-header-name {
    color: #999;
}
.board-canvas .js-list.list-wrapper:nth-child(6) textarea.list-header-name {
    color: #666;
}
.board-canvas .js-list.list-wrapper:nth-child(7) textarea.list-header-name {
    color: Black;
}
.list-wrapper {
    width: 296px;
    margin: 0 4px;
}
.list-wrapper.mod-add.is-idle {
    display: none;
}
/* display card id */

body .list-card-members {
    padding: 0px;
    margin: -4px -8px -8px 0px;
}
body .list-card-details .badges {
    padding: 0px;
    margin: 0px 0px 0px -4px;
}
body .card-short-id.hide {
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    height: 16px;
    line-height: 16px;
    padding: 1px 5px;
    font-weight: bold;
    font-size: 11px;
    background: none;
    border: solid 1px #ddd;
    border-radius: 3px 0;
    color: Gray;
}
body .has-cover .card-short-id.hide {
    border-radius: 0 0 3px 0;
}
/* bottom edit link */

body .list-card-operation {
    top: auto;
    right: 0px;
    bottom: 0px;
    padding: 1px;
}
body .list-card body .list-card {
    margin-top: 8px;
    transition: background-color ease-in-out .25s;
}
body .list-card.active-card {
    background-color: #eee;
    transition: background-color ease-in-out .25s;
}
body .list-card:first-child {
    margin-top: 2px;
}
/* display total cards count */

body .list-header-num-cards.hide {
    display: inline-block;
    font-size: 12px;
}
body .list-card-title {
    margin: 14px 0 5px 0;
}
body .list-card-title.js-card-name {
    font-size: 1em;
    font-weight: bold;
    line-height: 1.3;
    overflow: hidden;
    max-height: 20px;
    max-width: 272px;
    white-space: nowrap;
    text-overflow: ellipsis;
    color: #000;
}
body .list-header-extras-menu.dark-hover.js-open-list-menu {
    opacity: 0;
}
body .list-header-extras-menu.dark-hover.js-open-list-menu:hover {
    opacity: 1;
}
body .open-card-composer {
    max-height: 0px;
    opacity: 0;
    font-weight: normal;
    color: Black;
    transition: max-height ease-in-out .25s, opacity ease-in-out .1s;
}
body .open-card-composer:hover {
    max-height: 18px;
    opacity: 1;
    transition: max-height ease-in-out .25s, opacity ease-in-out .5s;
}
.card-label-red {
    background-color: Red;
}
 
.card-label.mod-card-front {
    max-width: none !important;
}
  
}
