css:
@-moz-document domain("christianforums.com") {
/* colors 1 -> 4 = light to dark 
   colors 3 and 4 now same */

/* THREAD */

div#headerMover {   /*PAGE BACKGROUND 3*/
    background-color: #104e8b /*#135da6*/ !important
}

/* Adjusted 10/13/16: */
div.thread_view {  /* THREAD WIDTH*/
  /*  width: 800px; */
}

div.pageContent {   /*masthead; table highlights 2 */
    background-color: #5783ad /*#1978d6*/ !important
}
li.message,
div.messageInfo.primaryContent  {             /* POST 1 */
    background-color: #b7c9dc /*#a5d2ff */
}


div.messageHeading,
div.navTabs,
h3.subHeading,
div[class*="categoryStrip"],
dl.sectionHeaders {      /*HEADERS 4 */
    background-color: #104e8b !important
}
div.messageUsserBlock div.extraUserInfo {
    background-color: red !important
}

li[class*="sticky"] a:link,
li[class*="sticky"] {   /* STICKY TOPICS */
    color: gray !important;
    font-size: 14px
}

div[id*="featured-thread"] { /* FEATURED THREAD */
    display: none
}
#pageDescription,
#pageDescription a  {  /* DESC. UNDER THREAD TITLE*/
    color: black !important
}                            

body[contenteditable="true"],
iframe[class*="MessageEditor"],
div.placeholder,
div.redactor_box     { /* EDIT BOX */
    background-color: #b7c9dc
}                                            
/* NW 

div[class*="nodeInfo"] {
    background-color: #147079
} */

}
