css:
@-moz-document domain("phoenix.corvidae.org") {
/* that pointless pip mark thing distinguishing members and 'advanced members' */

.ipsPip {
    display: none;
}
/* big names on posts */

strong[itemprop="name"] {
    font-size: 18px;
}
/* Most of the other fonts on the post author pane */

ul.cAuthorPane_info .ipsResponsive_hidePhone,
ul.cAuthorPane_info .ipsType_light,
ul.cAuthorPane_info > li {
    font-size: 11px;
    line-height: 15px;
}
/* Hide the "Member" group */

ul.cAuthorPane_info > li:nth-child(4) {
    font-size: 0px;
    line-height: 0px
}
/* But keep mods around */

ul.cAuthorPane_info > li > span[style] {
    font-size: 12px;
    line-height: 15px
}
/* PADDING */
/* Main author pane */

.cAuthorPane {
    padding: 5px 5px !important;
}
/* around avatar */

.cAuthorPane_photo {
    margin: 5px 0;
}
/* top of post content */

.cPost .ipsComment_meta {
    padding: 10px 5px 5px 0;
}
/* comment body */

.cPost .cPost_contentWrap {
    padding: 5px 10px 10px 0;
}
/* vertical space between comments */

.ipsComment {
    margin-bottom: 10px;
}
/* Those funny margins around the comment and other feeds */

.ipsPad {
    padding: 10px;
}
}
