css:
@-moz-document domain("community.bethesda.net") {
/* =========================================== */
/* Post View */
/* =========================================== */

/* banner */
#banner-thread {
 	background-image: none !important;
    background-color: #cccccc;
    height: 5px;
}

/* Make things not flat */
#jive-replies,
.original-post {
    box-shadow: 2px 10px 5px 5px #CCC;
    -webkit-box-shadow: 2px 2px 8px 5px #CCC;
}

/* Fixes wasted space between replies */
.jive-discussion-replies li.reply {
	margin-top: 0 !important;
}

/* Fixes reply-to being under avatars */
.j-thread-replyto {
    margin-left: 32px;
    top: -8px;
    position: relative;
}

/* Moves header up into the... header. OMG What!? */
.jive-thread-messages #jive-replies .j-thread-post > header {
    padding-bottom: 0 !important;
}
.jive-thread-messages #jive-replies .j-thread-post > header > h6 {
    top: -1.45em;
    position: relative;
    font-size: 15px;
    z-index: 999;
    margin-left: 18em;
}
.font-color-meta-light, a.font-color-meta-light:link, a.font-color-meta-light:visited {
    color: #fff !important;
}

/* Makes post backgrounds not searing to look at */
.jive-thread-messages #jive-replies .j-thread-post,
.j-thread .jive-content .j-thread-post {
    background-color: #ededed;
}

/* Gets rid of some of the extra spacing at the top of post content bodies */
.jive-rendered-content {
	padding: 0;
}


/* This encompasses all content in posts. Probalby elsewhere too, but oh well */
p {
	margin-bottom: .5em;
	padding-left: 1.3em;
    text-indent: -1.2em;
}
/* Get rid of people who add spaces between paragraphs */
p[style="min-height: 8pt; padding: 0px;"] {
  display: none;
}

.j-td-title a {
	padding-top: 1em;
    padding-bottom: 1em;
    padding-right: 100%;
}
}

@-moz-document domain("community.bethesda.net") {
/* =========================================== */
/* Main Page */
/* =========================================== */

#welcome-container, #jive-widgetframe_13789 {
    display: none;
}

/* Main list shifted left */
#jive-body-layout-ls .jive-body-layout-l .jive-widget-container-large {
    margin-left: 260px;
}

/* Left bar moved to right */
#jive-body-layout-ls .jive-body-layout-s {
    /* float: right; */
    padding-right: 30px;
    padding-left: 0;
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
}
}

@-moz-document domain("community.bethesda.net") {
/* =========================================== */
/* Community Pages */
/* =========================================== */

/* Do everything we can to hide the giant space wasting background header image thing */
#banner-text-overlay {
    display: none;
}
.j-body-place #jive-body .j-page-header {
    background-image: none;
    height: 5px; 
}

/* Shift the main content up so there is more real content above the fold. Gosh */
.j-body-place #j-main .j-layout {
    margin-top: -20px;
}
#jive-body-layout-sls .jive-body-layout-s1, #jive-body-layout-sls .jive-body-layout-s2 {
    margin-top: 45px;
}

/* removes egregious margin good golly */
.jive-widget {
    margin-bottom: -10px;
    z-index: 1;
}

/* Fix the Explore Community options */
.j-spaceGrid li {
	width: 30%;
}
.j-spaceGrid .j-place-card {
    border-radius: 5px;
}
.j-spaceGrid .j-place-img {
    display: none;
}
.j-spaceGrid li header h5 {
    margin-bottom: 0;
}
.j-spaceGrid h5 span {
    width: 100%;
}
}
