css:
@-moz-document url-prefix("http://yun.baidu.com/s/"), url-prefix("https://yun.baidu.com/s/"), url-prefix("http://pan.baidu.com/share/"), regexp("http://yun.baidu.com/s/(\\d\\D+\\d\\D)\\?[fid]+=\\d+") {
/*========= Public ========= */

@font-face {
	font-family: 'Open Sans';
	font-style: normal;
	font-weight: 400;
	src: url(http://fontstatic.useso.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff');
}
@font-face {
	font-family: 'Material Icons';
	font-style: normal;
	font-weight: 400;
	src: url(http://fontstatic.useso.com/s/materialicons/v14/2fcrYFNaTjcS6g4U3t-Y5ZjZjT5FdEJ140U2DJYC3mY.woff2) format('woff2');
}
@-webkit-keyframes bounceInRight {
	from, 60%, 75%, 90%, to {
		-webkit-animation-timing-function: cubic-bezier(.12, 1.10, .45, 1);
		animation-timing-function: cubic-bezier(.12, 1.10, .45, 1);
	}
	from {
		opacity: 0;
		-webkit-transform: translate3d(3000px, 0, 0);
		transform: translate3d(3000px, 0, 0);
	}
	60% {
		opacity: 1;
		-webkit-transform: translate3d(-25px, 0, 0);
		transform: translate3d(-25px, 0, 0);
	}
	75% {
		-webkit-transform: translate3d(10px, 0, 0);
		transform: translate3d(10px, 0, 0);
	}
	90% {
		-webkit-transform: translate3d(-5px, 0, 0);
		transform: translate3d(-5px, 0, 0);
	}
	to {
		-webkit-transform: none;
		transform: none;
	}
}
.share-person-username {
	animation-duration: 1s;
	animation-fill-mode: both;
	animation-name: bounceInRight;
}
.share-person-intro {
	margin-left: 10px;
}
.global-clearfix {
	margin-left: 10px;
}
.bd-aside {
	position: absolute;
	right: -220px;
	/*background:blue;*/
	/*border-top-style:dotted;*/
}
.share-operate-seprate,
.funcs-comment {
	display: none !important;
}
#bd-main {
	background: #FFFFFF;
}
.module-hot-rec {
	display: none !important;
}
#doc {
	background: #F7F7F7;
}
/*Head*/

.navs > a:last-child {
	display: none !important;
}
.info > ul li:nth-child(3) {
	display: none !important;
}
.info > ul li:nth-child(4) {
	display: none !important;
}
.info > ul li:nth-child(5) {
	display: none !important;
}
/*Right on Head*/

#hd a.def-nav.def-nav-share {
	display: none !important;
}
#hd a.li.xiaodu
{
	display:none;

}
#hd a.li{
	width:80px;
	height: 14px;
	text-align: right;
	border-bottom-style:solid;
	border-bottom-width: 1px;
	border-radius:0;

}

#hd span.icon
{
display:none;
}
#hd .current > a.pulldown-nav {
	display: none;
}
#hd a.li{
	float:none;
}
#hd .content{
	left:-15.1%;
	top:-50px;
	box-shadow: none;
	border: none; 
	padding:0 !important;
    	margin: 0 !important;
	
}
#hd .app-url:after{
     content: "功能";
	background: rgba(0, 0, 0, .6) !important;
	position: absolute !important;
	line-height: 25px;
	font-size: 15px;
	left: -24%;
	top: -52.5%;
	color:#FFFFFF;
	white-space: nowrap;
	transform: translate(50%, 50%);
	box-shadow: 0 2px 4px rgba(0,0,0,.2);
}
#hd .current:before {
	content: "\e8b8";
	font-family: 'Material Icons' !important;
	color: #555;
	position: absolute !important;
	left: 45%;
     top: 50%;
	transform: translate(-45%, -50%);
	
}
#hd .current{
	border-radius:2px;
	font-size: 30px;
	background: #0099FF!important;
	height: 50px !important;
	width: 50px !important;
	position: absolute !important;
	top: 100%;
	left:0%;
	box-shadow: 0 2px 4px rgba(0,0,0,.2);

}
#hd .separate {
	display: none !important;
}
#hd a.def-nav {
	background: #f1f1f1;
	background-image: url(http://7xtfu3.com1.z0.glb.clouddn.com/monuments.png);
	width: 50px;
	text-decoration: #f1f1f1;
	font-size: 0 !important;
	margin-top: -15px;
	/*没有外链的后果！*/
	
	padding-bottom: 10px;
	/*没有外链的后果！*/
	
	padding-right: 15px;
	/*没有外链的后果！*/
}
#hd .f-icon.logo {
	background-image: url(http://7xt3k7.com2.z0.glb.clouddn.com/dmlg.svg);
	margin-top: -5px;
}
#hd .module-header {
	box-shadow: none;
}
#hd .module-header-wrapper {
	background: #F7F7F7 !important;
}
#hd .module-header.hd-main.clearfix {
	background: #f1f1f1;

	min-width: 1366px;
	padding-top: 10px;
}
}
