css:
@-moz-document url-prefix("http://www.computerforums.org/forums"), url-prefix("https://www.computerforums.org") {
body {
    background: #DDDDDD;
}

img {
    border: none;
}

img[src="http://www.computerforums.org/forums/clear.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 0px !important;
    padding-top: 0px !important;
    background: url(http://s31.postimg.org/r5am37q47/navbits_finallink_ltr.gif) no-repeat !important;
}


#sk_header {
    border-bottom: 10px solid rgba(96, 96, 96, 1) !important;
}

a:link, body_alink {
    color: #000000;
}

a:visited, body_avisited {
    color: #000000;
    text-decoration: none;
}

.thead {
    background: #606060;
}

.tcat {
    background: #000000;
}

.tfoot {
    background: #000000;
}

.vbmenu_control {
    background: #000000;
}

#sk_header {
    background: #000000;
}

.vBulletin_editor {
    background: #606060;
    padding: 2px;
}

img[title="Go to last post"] {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 17px !important;
    padding-top: 17px !important;
    background: url(http://s31.postimg.org/pma4dl0qf/lastpost.gif) no-repeat !important;
}

img[src="http://www.computerforums.org/forums/iconimages/software-operating-systems_ltr.gif"] {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 30px !important;
    padding-top: 17px !important;
    background: url(http://s31.postimg.org/r5am37q47/navbits_finallink_ltr.gif) no-repeat !important;
}

img[src="http://www.computerforums.org/forums/images/misc/navbits_finallink_ltr.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 30px !important;
    padding-top: 17px !important;
    background: url(http://s31.postimg.org/r5am37q47/navbits_finallink_ltr.gif) no-repeat !important;
}


img[src="http://www.computerforums.org/forums/images/misc/navbits_start.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 30px !important;
    padding-top: 15px !important;
    background: url(http://s31.postimg.org/la0w4a03b/navbits_start.gif) no-repeat !important;
}

.tborder {
    border: 2px solid #606060;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
}

.panelsurround {
    background: #606060
}


.panel {
    background: #eeeeee;
    padding: 10px;
    border: 2px outset;
}

img[src="http://www.computerforums.org/forums/images/buttons/newthread.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 120px !important;
    padding-top: 28px !important;
    background: url(http://s31.postimg.org/guu3kj8af/reply_1.gif) no-repeat !important;
}



img[src="http://www.computerforums.org/forums/images/misc/logo.png"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 500px !important;
    padding-top: 84px !important;
    background: url(http://s32.postimg.org/78u0mdd9x/logo_new3.png) no-repeat !important;
}

.tcat a:hover,.tcat a:active,.tcat_ahover{color:hsl(215,39%,57%)!important;} 

.tfoot a:hover, .tfoot a:active, .tfoot_ahover {
    color: #668ABC;
}

.thead a:hover, .thead a:active, .thead_ahover {
    color: #668ABC;
}

.time {
    color: #6060B0;
}

img[src="http://www.computerforums.org/forums/images/misc/menu_open.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 11px !important;
    padding-top: 7px !important;
    background: url(http://s32.postimg.org/m2yzu2fzl/menu_open.gif) no-repeat !important;
}

img[src="http://www.computerforums.org/forums/images/buttons/collapse_tcat.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 9px !important;
    padding-top: 9px !important;
    background: url(http://s32.postimg.org/9kv9fyp9t/collapse_tcat.gif) no-repeat !important;
}

img[src="http://www.computerforums.org/forums/images/reputation/reputation_pos.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 8px !important;
    padding-top: 10px !important;
    background: url(http://s32.postimg.org/8prt85rox/reputation_pos_1.png) no-repeat !important;
}

img[src="http://www.computerforums.org/forums/images/statusicon/user_online.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 15px !important;
    padding-top: 15px !important;
    background: url(http://s32.postimg.org/k9sgzhv69/user_online4.png) no-repeat !important;
}

img[src="http://www.computerforums.org/forums/images/statusicon/user_offline.gif"] {
    height: 0 !important;
    width: 0 !important;
    padding-left: 15px !important;
    padding-top: 15px !important;
    background: url(http://s32.postimg.org/wogo9hdzl/user_offline3.png) no-repeat !important;
}
}

@-moz-document url-prefix("http://www.computerforums.org/forums"), url-prefix("https://www.computerforums.org") {
#nav_wrap li.current a,
#nav_wrap li a:hover {
    background: #606060;

}
}
