css:
@-moz-document domain("forums.rocketshoppe.com"), domain("www.oldrocketforum.com") {
/* default link colors for dark areas */
a:visited,
a:link {
    color: white;
}

/* A little highlight for hovering.  Shows an unwanted background color on "post reply" buttons
   but I can't figure out how to eliminate that without breaking other things */
a:hover {
    background-color: #BBB;
}

/* special handling for image links */
a:hover img {
    background-color: #10375D;
}

#controlbar a:hover img {
    background-color: #C2BDA3;
}

.alt1 a:hover img,
.alt1Active a:hover img {
    background-color: white;
}

/* Link colors inside light areas */
.alt1 a:visited,
.alt1 a:link,
.alt1Active a:visited,
.alt1Active a:link,
.alt2 a:visited,
.alt2 a:link,
.alt2Active a:visited,
.alt2Active a:link,
td.controlbar a:visited,
td.controlbar a:link {
    color: #313;
}

/* Main content areas */
.alt1,
.alt1Active,
/*[id^=collapseobj_forumbit] .alt2,
[id^=collapseobj_forumbit] .alt2Active,*/
textarea {
    background-color: white;
    color: black;
    font-weight: normal;
}

/* Post title bar and some other areas */
.alt2,
.alt2Active,
.bginput {
    background-color: #EDF5F8;
    color: black;
    font-weight: normal;
}

/* User name at the top of each post */
.bigusername:visited,
.bigusername:link {
    color: black;
}

/* Without boldface, slightly larger font is a bit easier on the eyes */
textarea {
    font-size: 12px;
}

/* This is the background for the reply areas at the bottom of a page, made a little darker to 
   be clearly distinguishable from the main content areas.  It's just dark enough that the white
   legends still look good, but black text is also adequately contrasty and readable */
.panelsurround,
.panel {
    background-color: #98C2CD;
}

/* No need for time to be a different color than other text, but we'll make it bold to stand out a bit */
.time {
    color: inherit;
    font-weight: bold;
}

/* replace the horrible background image on a couple of headers */
.tcat {
    background: rgba(231,241,245,1);
    background: -moz-linear-gradient(top, rgba(231,241,245,1) 0%, rgba(196,205,207,1) 100%);
    background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(231,241,245,1)), color-stop(100%, rgba(196,205,207,1)));
    background: -webkit-linear-gradient(top, rgba(231,241,245,1) 0%, rgba(196,205,207,1) 100%);
    background: -o-linear-gradient(top, rgba(231,241,245,1) 0%, rgba(196,205,207,1) 100%);
    background: -ms-linear-gradient(top, rgba(231,241,245,1) 0%, rgba(196,205,207,1) 100%);
    background: linear-gradient(to bottom, rgba(231,241,245,1) 0%, rgba(196,205,207,1) 100%);
/*    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#e7f1f5', endColorstr='#c4cdcf', GradientType=0 ); */
}
}
