css:
@-moz-document url-prefix("https://web.telegram.org") {
  
/* TELEGRAM CUSTOM DARK THEME */

html {
    background: #000 url(https://img-fotki.yandex.ru/get/5801/59844696.0/0_a1e22_6b2be5ae_orig) fixed center no-repeat;
}
.im_page_wrap {
    background: transparent !important;
    border: none !important;
    box-shadow: none !important;
}
body,
a.tg_checkbox,
.contacts_modal_members_list a.contacts_modal_contact,
a.tg_radio {
    color: #fff !important;
}
.im_dialogs_col_wrap {
    border: none !important;
}
.tg_head_split,
.im_dialogs_col,
.im_history_message_wrap.ng-scope,
.im_dialogs_modal_col_wrap .im_dialogs_search_field {
    background: transparent !important;
}
.dropdown.open .tg_head_btn,
.tg_head_btn:hover {
    background: none !important;
    box-shadow: inset 0 0 9px 4px rgba(255, 255, 255, 1) !important;
    transition: all -1ms cubic-bezier(0.77, 0, 0.175, 1);
}
.dropdown.open .tg_head_btn,
.tg_head_btn {
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}
.dropdown-menu {
    background: url(https://img-fotki.yandex.ru/get/6802/59844696.0/0_9cebb_719356a2_orig) !important;
    border: none !important;
}
.tg_head_logo_dropdown .dropdown-menu > li > a:hover,
.tg_head_peer_dropdown .dropdown-menu > li > a:hover {
    background: none !important;
    box-shadow: inset 0 0 9px 4px rgba(255, 255, 255, 1) !important;
    transition: all -.1s cubic-bezier(0.77, 0, 0.175, 1);
}
.tg_head_logo_dropdown .dropdown-menu > li > a,
.tg_head_peer_dropdown .dropdown-menu > li > a {
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}
/*.im_message_author,
.im_message_reply_author,
.im_message_fwd_author {
    background: #fff !important;
}*/

.im_dialogs_search_field {
    background-color: rgba(0, 0, 0, 0) !important;
    border-color: blueviolet !important;
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}
.im_dialogs_search_field:active,
.im_dialogs_search_field:focus {
    color: #fff !important;
    box-shadow: 0 0 9px 4px rgb(149, 108, 218) !important;
}
.im_dialog_peer {
    color: #fff
}
.im_dialogs_scrollable_wrap .active a.im_dialog {
    background-color: rgba(0, 0, 0, 0.7);
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}
.im_dialogs_scrollable_wrap a.im_dialog:hover,
.im_dialogs_scrollable_wrap a.im_dialog_selected,
.contacts_modal_members_list .active a.contacts_modal_contact,
.contacts_modal_members_list .active a.contacts_modal_contact:hover,
.contacts_modal_members_list a.contacts_modal_contact:hover {
    background: rgba(0, 0, 0, 0.80);
    transition: all -.1s cubic-bezier(0.77, 0, 0.175, 1);
}
.im_dialogs_scrollable_wrap .active a.im_dialog:hover,
.im_dialogs_scrollable_wrap .active a.im_dialog_selected {
    background-color: #000;
}
.im_dialogs_scrollable_wrap a.im_dialog {
    transition: all 250ms cubic-bezier(0.77, 0, 0.175, 1);
}
a,
.im_dialog_chat_from_wrap,
.im_dialog_message_media,
.im_dialog_message_service {
    color: #06C7F5;
}
a:focus,
a:hover {
    color: #00FFF7
}
.composer_rich_textarea,
.composer_textarea {
    box-shadow: none;
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}
.composer_rich_textarea:focus,
.composer_textarea:focus {
    box-shadow: 0 0 9px 4px rgb(0, 148, 255) !important;
}
a.composer_emoji_btn:hover {
    background: rgba(151, 110, 230, 0.5) !important;
}
.icon-tooltip-tail {
    background: rgb(10, 154, 243) !important;
    border: none !important;
}
.composer_emoji_insert_btn {
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}
.modal-content {
    background: url(https://img-fotki.yandex.ru/get/5103/59844696.0/0_9ceba_e7ad3885_orig);
}
.md_modal_head {
    background: url(https://img-fotki.yandex.ru/get/6836/59844696.0/0_9b726_2ea2b3ba_orig)
}
.form-control {
    border-color: #000 !important;
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}
.form-control:focus {
    color: #fff !important;
    box-shadow: 0 0 9px 4px rgb(149, 108, 218) !important;
}
.md_modal_list_peer_description .status_online,
.md_modal_list_peer_description.status_online {
    color: #0BF315;
}
.im_history_selectable .im_message_outer_wrap:hover {
    background: rgba(0, 0, 0, 0.90) !important;
}
.im_message_selected .im_message_outer_wrap,
.im_message_focus .im_message_outer_wrap {
    background: rgba(0, 0, 0, 0.90) !important;
}
/*.im_message_reply_author.ng-scope {
    width: 340px !important;
}
*/

.im_message_wrap {
    background: rgba(0, 0, 0, 0.90) !important;
}
.md_modal_section_splitter {
    box-shadow: none !important;
    background: none !important;
}
.im_service_message {
    background: rgba(0, 0, 0, 0.75);
}
.im_message_unread_split {
    background: rgba(0, 146, 255, 0.7) none repeat scroll 0% 0%;
    color: rgb(255, 255, 255);
}
.im_history_col .nano > .nano-pane,
.contacts_modal_col .nano > .nano-pane,
.sessions_modal_col .nano > .nano-pane,
.im_dialogs_modal_col .nano > .nano-pane {
    transition: all 1000ms cubic-bezier(0.77, 0, 0.175, 1) !important;
}
.composer_emoji_tooltip_shown {
    background: url(https://img-fotki.yandex.ru/get/6835/59844696.0/0_9b72e_d5fcdf36_orig) !important;
    box-shadow: 0 0 9px 4px rgb(10, 154, 243) !important;
    transform: scaleX(1) !important;
}
.composer_emoji_tooltip {
    display: block !important;
    transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
    background: none;
    box-shadow: none;
    border: none !important;
    transform: scaleX(0);
    margin-left: -122.5px !important;
}
  
/* TELEGRAM MORE WIDTH */

.im_page_wrap {
    max-width: 95%;
}
.tg_head_split {
    max-width: 95%;
}
.tg_head_logo_wrap {
    width: 21%;
}
.im_message_wrap {
    max-width: 95%;
}
.im_dialogs_col_wrap {
    width: 20%;
}
.im_send_panel_wrap {
    max-width: 100%;
}
.im_send_form {
    max-width: 100%;
}
}
