css:
@-moz-document domain(youtube.com) {
#watch7-sidebar-contents,
#footer,
#watch-discussion,
.branded-page-related-channels,
#description-text,
.style-scope ytd-comments,
#related,
#info,
ytd-video-owner-renderer,
#subscribe-button,
ytd-channel-video-player-renderer,
#subscriber-count,
#tabs-container
{
    display: none !important;
}

img#img {
    opacity: 0.01;
}

yt-formatted-string.content.content-line-height-override.style-scope.ytd-metadata-row-renderer {
    display: none;
}

#watch7-content
{
    margin: 0px auto;
}

#body-container
{
    padding-bottom: 0px;
}

td-browse.style-scope.ytd-page-manager {
    display: none;
}

#masthead-container, a.ytp-next-button, #guide, .banner-visible-area, ytd-shelf-renderer {
    display: none !important;
}

#header {
    margin-top: 0px !important;
}

#content {
        margin-top: -56px;
}

app-toolbar.style-scope.ytd-c4-tabbed-header-renderer {
    display: none;
}

}
