css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("del.icio.us") {
  
  /* Widen Container */
  div.profileContent div.container {padding-right: 10px !important;}
  div.profileLeftPan {width: 20% !important;}
  div.profileMidpanel {width: 80% !important; padding: 0 10px 0 40px !important;}
  div.contentPanel {padding: 0 !important;}
  div.tabSubMenu {padding: 0 !important;}  

  /* Header */
  div.header {padding: 5px 0 !important; box-shadow: none !important;}
  div.breadcrumbPanel {padding: 0 !important;}
  
  /* Left Sidebar */
  .profileInfo {
    box-shadow: none !important;
    padding: 0 !important;
    background: transparent !important;
  }
  div.profileBrief h2, h3 {
    font-size: 16px !important;
    line-height:normal !important;
    padding-bottom: 0 !important;
  }
  .shuffleFontSize {background: transparent !important;}
  
  /* Tags Cloud */
  .shuffleFontSize {padding: 0 !important;} 
  .shuffleFontSize > a {font-size: 13px !important; color:#17538d !important;}
  .shuffleFontSize > a:hover {color:#0076e8 !important;}
  
  /* Search Bar */
  div.tagsSeacrhPanel input {
    background: #FFF !important;
  }  
  input.searchInput {   
    height: auto !important;
    padding: 1px 5px !important;
    background: #FFF !important;
    box-shadow: none !important;
    border-radius: 0 !important;    
    line-height: 20px !important;
  }
  div.searchPanel button {
    padding: 0 !important;
    position: absolute !important;
    top: 2px !important;
    right: 2px !important;
    border-radius: 0 !important;
    font-size: small !important;    
    line-height: 18px !important;    
  }
  
  /* Link */
  div.thumbTBriefTxt, ul.tagName, div.articleThumbBlock, 
  div.articleTitlePan, div.contentPanel h3, div.sharePanel {padding:0 !important;}
  div.articleThumbBlock {background: transparent !important;}
  div.contentPanel p {line-height:normal !important;}
  div.articleThumbBlockOuter {border-bottom: 1px solid #c7c7c7 !important;}
  
  /* Link - Title */
  div.articleThumbBlock h3 a {
    font-size: 18px !important; 
    font-family: 'HelveticaNeueLTPro-Bd', "Microsoft YaHei", sans-serif;
  }
  /* Link - Url */
  a.url,  ul.tagName li a {color: #868484 !important; font-size: 13px;  text-decoration: none !important;}
  a.url:hover,  ul.tagName li a:hover {color: #0076e8 !important;}
  
  /* Link - Tags */
  div.thumbTBriefTxt {text-align: right!important;
    position: absolute!important;
    right: 0 !important;
    top: 22px!important;    
  }

  /* Link - Checkbox */
  div.articleTitlePan input, div.tabSubMenu input {right: 0 !important; left: auto !important;}
  
  /* Link - Control */
  div.sharePanel {border: none !important; background: transparent !important;display: none;}
  div.articleThumbBlockOuter:hover > div.sharePanel {display: block;}
  div.sharePanel ul li a:hover {color: #868484 !important;}
  div.sharePanel ul li a {color: #c7c7c7 !important;}

  /* Hide Ads*/
  div[class^="googleAd"], div[class^="ad"], div.contentRightPan {display: none !important;}
  /* Hide Silly Things */
  div.tabMenu, div.profilePic, div.profileMidpanel h1, .tooltip {display: none !important;}
  
  /* Link - Info (If do not apply JS) */
  div.savesCount,
  div.articleInfoPan p:nth-child(2),
  div.articleInfoPan p:nth-child(3){
    display: none !important;
  }
  div.articleInfoPan {
    padding: 0 !important;   
    min-height: 0 !important;
  }
  div.articleInfoPan p {padding: 0 !important;}
  
}
