css:
@-moz-document domain("biblegateway.com") {
section.passage.single-translation.content-section {
    background-color: #474748
}
span.heading { color: black }
/*SINGLE VERSION */

body > section.passage.single-translation.sidebar-info.show-alc.narrow-resources.content-section > div > div > div.passage-main > div > div.passage-resource-table > div.passage-bible > div,
body > section.passage.single-translation.sidebar-info.show-alc.narrow-resources.content-section > div > div > div.passage-main > div > div.passage-resource-table > div.passage-bible > div > div.passage-scroller-top-wrapper,
body > section.passage.single-translation.sidebar-info.show-alc.narrow-resources.content-section > div > div > div.passage-main > div > div.passage-resource-table > div.passage-bible > div > div.passage-scroller-top-wrapper > div {
    width: 800px
}
div.passage-resources {
    background-color: #444 !important
}
div.passage-resource-table {
    width: 100%
}
div.passage-main-wrap,
div.passage-text,
div.passage-scroller-top,
body > section.passage.single-translation > div > div > div.passage-main > div > div.passage-box-wrap > div > div.passage-table > table > tbody > tr > td > div.passage-text {
    background-color: #96D4D4;
    background-size: cover;
}
/* FIRST COLUMN */
td.passage-col.col-xs-6.first > div.passage-text,
td.passage-col.col-xs-4.first > div.passage-text {
    background-color: #BDDADA !important
}
/* LAST COLUMN */
td.passage-col.col-xs-6.last > div.passage-text,
td.passage-col.col-xs-4.last > div.passage-text {
/*    background-image: URL(https://dl.dropboxusercontent.com/u/4752047/violet-soft.jpg);*/
    background-color: #EABDBD !important;
    background-size: cover
}

div[class*="MOUNCE"] p {
    line-height: 1 !important;
    font-size: 12px !important; 
}

/* MIDDLE COLUMN */
td.passage-col.col-xs-4 > div.passage-text {
    background-color: lightgray !important
}
}
