css:
@-moz-document domain("x.sankuai.com") {
/*隐藏左侧反馈按钮、底部logo*/
.left-menu-wrapper,
.logo-ft {
    display: none;
}
/*隐藏下载app二维码*/
.downloadapp {
    display: none;
}
#ft{
	display:none !important;
}

/*设置背景图片，全局字体*/
body {
    background-image: url(https://res.wx.qq.com/zh_CN/htmledition/v2/images/web_wechat_login_bg28a2f7.jpg);
	background-repeat: no-repeat;
	    font-family: "Helvetica Neue", Helvetica, "Hiragino Sans GB", "Microsoft YaHei", "微软雅黑", Arial, sans-serif;
}

/*设置标签按钮颜色*/
.panel-left-chat .panel-list-menu.tab-chat .menu-tab-item-chat .icon-tab, .panel-left-chat .panel-list-menu.tab-contact .menu-tab-item-contact .icon-tab, .panel-left-chat .panel-list-menu.tab-room .menu-tab-item-room .icon-tab {
	color: #3CAF36;
}
.dx-sh div.panel.panel-chat.css3-slide, .panel-chat {
    width: 1000px;
	border-radius:0px;
}
#dx_leftPanelChat ::-webkit-scrollbar {
    width: 0;
}

/* left */
.panel-chat .panel-list-content {
	border-radius: 0;
}
#dx_leftPanelChat .panel-left-chat {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
}

body{
-webkit-font-smoothing:antialiased;
}
#dx_chat{
padding:0;
}
.panel-chat .panel-list-content {
    border-bottom-right-radius: 0;
}
#dx_leftPanelChat, #ui_chart_panel {
    box-shadow: none;
}
#dx_leftPanelChat .panel-left-header {
    background: #2E3238;
	padding:18px;
    /*border-top-right-radius: 0;
    border-bottom-right-radius: 0;*/
    border-radius: 0;
}
#dx_tips {
	background: transparent;
    color: #000;
	font-size:18px;
}
.avatar{
	display:block;
	float:left;
}
#dx_profile .info{
	margin:5px 0 0 0;
}
.panel-chat .myProfile .nickName{
	font-size:18px !important;
	color: #fff;
}
.panel-chat .myProfile img {
    position:static;
	z-index: 100;
    width: 40px;
    height: 40px;
    border-radius: 2px;
    -moz-border-radius:2px;
    -webkit-border-radius: 2px;
	margin-right:10px;
    cursor: pointer;
	border:0;
}
.panel-chat .item-info-chat {
	background-color: #fff;
}
.panel-left-header .split{
	display:none;
}
.panel-left-header a.globalMenu{
	color:#777777;
}
.panel-chat .item-info-chat{
 	background-color:#2E3238;
	padding:12px 18px 11px;
    border-bottom:1px solid #292C33;
	border-top:0px;
}
/*search*/
.panel-left-chat .panel-search {
	border-top-color: #2E3238;
	border-bottom-color:#2E3238;
	background-color:#26292E;
}
.panel-left-chat .panel-search input:focus{
	background-color:transparent;
}
.panel-search input{
	color:#FFF;
}
.panel-left-chat .list-search{
	top:107px;
	left:0px;
	border:0px;
	box-shadow:0px 0px;
}
.list-info .vcard, .list-info .list{
	border-top:1px solid #ddd !important;
}
/*table*/
.panel-left-chat .panel-list-menu{
	height:40px;
	padding-bottom:4px;
	background-color:#2E3238;
	border-bottom:1px solid #24272C;
	box-shadow:0px 0px;
	left:0px;
}
.panel-left-chat .panel-list-menu .menu-tab{
	border-right:0px;
	box-shadow:0px 0px;
}
#dx_leftPanelContent{
	box-sizing:border-box;
	/*height:630px !important;*/
}
#dx_leftPanelChat .selected.item-info-chat {
    background: #3A3F45;
    color: #fff;
}
.panel-chat .selected .vcard .name, .panel-chat .selected .vcard .org, .panel-chat .selected.item-info-chat .info .desc, .panel-chat .selected.item-info-chat .info .name, .panel-chat .selected.item-info-chat .time {
    color: #fff;
	font-size:13px !important;
}
.panel-chat .item-info-chat .name,.panel-chat .selected .vcard .org{
	color:#FFF;
	font-size:13px !important;
}
.panel-chat .item-info-chat .desc,.panel-chat .item-info-chat .time{
	color:#989898;
	font-size:13px !important;
}
.contactListContent .tree, .panel-chat .contact-list, .panel-list-content .list-info {
	background-color: #2E3238;
}
.contactListContent h3{
	border-bottom:1px solid #26292E;
	background:#2E3238;
	color:#999;
	font-weight:400;
	text-shadow:#FFF 0px 0px 0px;
	box-shadow:0px 0px;
}
.contactListContent .tree h4{
	color:#999;
	font-weight:400;
	text-shadow:#FFF 0px 0px 0px;
	box-shadow:0px 0px;
	border-top:1px solid #26292E;
}
.list-info .vcard .detail, .list-info .vcard .name{
	color:#999;
}
.list-info .vcard,.list-info .list{
	border-top:1px solid #26292E;
}
.list-info .vcard.selected {
    background: #3A3F45;
}
.panel-chat .top-session::after{
	border:5px solid #3CAF36;
	border-bottom-color:transparent;
	border-left-color:transparent;
	right:0;
}
/*left end!*/
/*dialog*/
.dialog-person-vcard {
	box-shadow: none;
}
.window {
	border-radius: 0;
    background-color: #fff;
    border-top: 2px solid #3CAF36;
    box-shadow: none;
}
.dialog-person-vcard .window .content .panel-info-base .photo-vcard {
	border-radius: 70px;
}
.dialog-person-vcard .window .content .panel-info-description, .dialog-person-vcard .window .content .panel-info-base,
.window .content .detail {
	border-bottom-color: #FAFAFB;
    border-top-color: #FAFAFB;
}
.window .content .detail .vcard .avatar img {
	border-radius: 40px;
}
/*right*/
#ui_chart_panel {
    margin-left: 0;
}
.split.menu-split {
	display:none;
}
.panel-chat .chat {
    border-radius: 0;
}
.panel-chat .hd-chat-roster-title {
	background-color: #eeeeee;
    border-bottom-color: #d6d6d6;
    border-left: 1px solid #eee;
    background-image: none;
    border-top-right-radius: 0;
	height:46px;
}
.panel-chat .groupNameWrap {
	border-radius: 0;
    color: #000;
}
.panel-chat .panel-chat-wrap{
	box-sizing:border-box;
	/*height:685px !important;*/
}
.panel-chat-container .hd-chat-title {
	border-radius: 0;
	background-color:#EEEEEE;
	border-bottom:1px solid #d6d6d6;
	height:46px;
}
#dx_chatMainPanelWrap{
	width:100%;
}
.hd-chat-title .editChatTitleWrap input, .panel-chat .chatNameWrap .chatName{
	color:#000;
}
.contactListContent .tree>h4 .panel-chat-container .tree-icon, .new-dialog .dialog-title .panel-chat-container .close, .panel-chat-container .contactListContent .tree>h4 .tree-icon, .panel-chat-container .glyphicon, .panel-chat-container .list-roster .group-moderator .item-roster .group-identity, .panel-chat-container .new-dialog .dialog-title .close, .panel-chat-container .ui-qrcode .header .closeIcon, .panel-chat-container .window .content .detail .group-moderator .group-identity, .panel-chat-container .window .header .closeIcon, .ui-qrcode .header .panel-chat-container .closeIcon, .window .content .detail .group-moderator .panel-chat-container .group-identity, .window .header .panel-chat-container .closeIcon{
	color:#333;
}
.chatsplit #dx_chatMainPanelWrap{
	width:547px;
}
.panel-chat .chatNameWrap .chatName{
	font-family: Helvetica,Arial;
	padding-top: 12px;
	color: #000;
}
.hd-chat-title .editChatTitleWrap input {
	color: #000;
}
.panel-chat-container .menu-wrapper a, .panel-chat-container .btn-roster-list a {
	color: #000;
}
/*chat panel*/
.bubble-list .time p {
	background:rgba(220, 220, 220, 1.2);
	border-radius:3px;
	color:#FFF;
	text-shadow:0 0px 0 rgba(255,255,255,.7);
}
#ui_chart_panel #dx_chatContainer {
    border-radius: 0;
}
.bubble-list .time::before{
	display:none;
}
.panel-chat .panel-chat-wrap{
	background-color: #EEEEEE;
    /*background-image: url("http://ww1.sinaimg.cn/large/5e44810egw1f2s17mq8ofj20k00j1t9f.jpg");*/
    background-repeat: no-repeat;
    background-position: center;
}
.chatsplit .panel-chat-roster{
	background-color: #EEEEEE;
    background-repeat: no-repeat;
    background-position: center;
}
.nx_groupchat .item .talker-name {
	color: #4f4f4f;
}
.bubble-list .cont {
	background-color: #fff;
	border-radius:3px;
	border-color: #ffffff;
}
.bubble-list .cont:after {
	position: absolute;
    top: 14px;
    border: 6px solid transparent;
    content: " ";
    border-right-color: #ffffff;
    border-right-width: 6px;
    left: -12px;
}
.bubble-list .cont:before {
	top:12px;
	border:6px solid transparent;
	background:none;
	content: " ";
}
.bubble-list .me .cont {
	background-color: #B2E281;
	border-color: #20956d;
	border-width:0;
	color: #000;
	border-radius:3px;
}
.bubble-list .me .cont:after {
	position: absolute;
    top: 14px;
    border: 6px solid transparent;
    content: " ";
    border-left-color: #b2e281;
    border-left-width: 6px;
    left: 100%;
}
.bubble-list .me .cont::before{
	display:none;
}
.bubble-list .emotion:after {
	display:none;
}
/*input*/
#ui_chart_panel #chat_editor {
	background-color: #edecea;
    border-left: 1px solid #E5E6EA;
}
.panel-input-area .chatInput {
	box-shadow: none;
}
.panel-input-area .attach a {
    background: url("http://7j1wtm.com1.z0.glb.clouddn.com/sprite_sheet.png");
}
.panel-input-area button#sendMessage.btn-chatSend {
	background-image: none !important;
}
.panel-input-area button.btn-chatSend[disabled=disabled] {
	color: #fff;
    background-color: #b2e281 !important;
}
.panel-input-area button.btn-chatSend:hover {
	color: #000;
    background-color: #b2e281 !important;
}
.panel-input-area button.btn-chatSend[disabled=disabled] {
	color: #fff;
    background-color: rgba(0,0,0,0.3) !important;
}
.panel-input-area button.btn-primary {
	color: #000;
    background-color: #b2e281 !important;
}
.dx-chat-main-panel-wrap .plugin-button{
	display:none !important;
}
.panel-input-area .chatInput{
	width:520px;
}
.panel-chat .panel-input-area{
	border-top:1px solid #d6d6d6;
}
.chatListSearchInput input{
	color:#000;
}
body{
	height:100%;
}
#dx_chat{
	height:100% !important;
}
.css3-slide .panel-left-chat{
	height: 100%;
    overflow: hidden;
    box-sizing: border-box;
}
.panel-chat .panel-list-content{
	height:100% !important;
}
.panel-chat-bottom{
position: absolute;
    z-index: 11;
    width: 100%;
    bottom: 0;
    left: 0;
}
.panel-chat .chat{
	height:100%;
}
.panel-left-chat{
	border-radius:0;
}
#ui_chart_panel #dx_chatContainer{
	height:100%;
}
#dx_chatMainPanelWrap{
	height:100%;
}
.panel-chat .panel-chat-wrap{
	height:100% !important;
	padding-bottom:100px;
}
.panel-chat .panel-chat-top{
	height:100%;
}
.panel-chat-container .list-roster{
	height:100% !important;
}
/*.panel-chat-container{
	padding-bottom:50px;
}
.panel-chat .panel-chat-wrap{
	height:100% !important;
}*/
}
