css:
/* Edits to group page */

@-moz-document domain(roblox.com) {

	#left-column .StandardBox {
		border: 0px;
	}

	.tab {
		border: 1px solid #EFEFEF;
		border-bottom: 0px;
	}

	.tab active {
		border: 1px solid #D6D6D6;
		border-bottom: 0px;
	}

	#mid-column .StandardBox {
		border: 0px;
		border-top-right-radius: 10px;
		border-top-left-radius: 10px;
		width: 100% !important;
		margin-left: -5px !important;
	}

	textarea#ctl00_cphRoblox_GroupWallPane_NewPost {
		border: 2px solid rgba(187, 187, 187, 0.15);
	}

	input#ctl00_cphRoblox_GroupWallPane_NewPostButton {
		border: 2px solid rgba(200,200,200,0.5);
	}

	input#ctl00_cphRoblox_GroupStatusPane_StatusSubmitButton {
		border: 1px solid rgb(185, 185, 185);
		box-shadow: 1px 1px rgba(156, 156, 156, 0.52);
	}

	div#ctl00_cphRoblox_GroupWallPane_Wall {
		margin-top: 50px !important;
	}

	.StatusView .top {
		background: url('http://i.imgur.com/xSyYtKn.png') no-repeat;
		padding: 5px;
	}

	.StatusView .bottom {
		background: url('http://i.imgur.com/6qAdQwy.png') no-repeat;
		padding: 5px;
	}

	#ctl00_cphRoblox_GroupStatusPane_StatusView {
		width: 70%;
		border: 0px;
	}

	input#ctl00_cphRoblox_GroupStatusPane_StatusTextBox {
		border: 1px solid rgba(202, 202, 202, 0.5);
	}

	div#SearchControls {
		border: 0px;
		padding-top: 10px;
	}

	div#ctl00_cphRoblox_rbxGroupFundsPane_GroupFunds {
		padding-right: 0;
		border: 0px;
		padding-top: 10px;
		padding-bottom: 10px;
	}

	.GroupControlsBox {
		border: 0px;
		padding-bottom: 10px;
	}

	.btn-control btn-control-medium translate {
		border: 0px;
	}

	input#ctl00_cphRoblox_GroupAdminButton {
		border: 1px solid rgb(185, 185, 185);
		box-shadow: 1px 1px rgba(156, 156, 156, 0.52);
	}

	button.btn-control.btn-control-medium.translate {
		border: 1px solid rgb(185, 185, 185);
		box-shadow: 1px 1px rgba(156, 156, 156, 0.52);
	}

	input#ctl00_cphRoblox_MakePrimaryGroupButton {
		border: 1px solid rgb(185, 185, 185);
		box-shadow: 1px 1px rgba(156, 156, 156, 0.52);
	}

	input#ctl00_cphRoblox_LeaveButton {
		border: 1px solid rgb(185, 185, 185);
		box-shadow: 1px 1px rgba(156, 156, 156, 0.52);
	}

	button.btn-control.btn-control-medium.translate {
		border: 1px solid rgb(185, 185, 185);
		box-shadow: 1px 1px rgba(156, 156, 156, 0.52);
	}

	button.btn-control-medium.translate.btn-control-disabled {
		border: 1px solid rgb(185, 185, 185);
		box-shadow: 1px 1px rgba(156, 156, 156, 0.52);
	}

	input#ctl00_cphRoblox_GroupSearchBar_SearchKeyword {
		border: 1px solid rgba(202, 202, 202, 0.5);
	}

	input#ctl00_cphRoblox_GroupSearchBar_SearchButton {
		border: 1px solid rgba(202, 202, 202, 0.5);
	}

	input#ctl00_cphRoblox_GroupSearchBar_SearchButton:hover {
		background: #D0D0D0;
	}

	div#BodyWrapper {
		background: rgba(0, 0, 0, 0);
	}

	div#RepositionBody {
		background: rgba(255, 0, 0, 0);
	}

	div#Body {
		position: relative;
		top: 25px;
	}

	div#CreateGroupBtn {
		border: 0px;
	}

	img {
		background-color: rgba(0, 0, 0, 0) !important;
		border: 0px !important;
	}

	div#ctl00_cphRoblox_rbxGroupFundsPane_GroupFunds b {
		position: relative;
		left: 5px;
	}

	div#ctl00_cphRoblox_rbxGroupFundsPane_GroupFunds span {
		margin-left: 10px !important;
	}

	.GroupListItemContainer:hover, .GroupListItemContainer.selected:hover {
		background-color: #E8E8E8;
		cursor: pointer;	
	}

	div#Body {
		background: rgba(250, 250, 250, 1);
	}

	.AlternatingItemTemplateOdd {
		width: 100% !important;
		margin-left: -5px !important;
	}

	div#GroupsPeoplePane_Games{
		background-color: rgb(250, 250, 250);
		border-top: 1px solid #D6D6D6;
	}

	.GroupPanelContainer {
		background: rgba(239, 239, 239, 0.5) !important;
	}

	select#ctl00_cphRoblox_rbxGroupRoleSetMembersPane_dlRolesetList {
		margin-top: 10px;
		border: 1px solid rgba(200, 200, 200, 0.5);
	}

	.GroupMember:hover {
		background: rgba(234, 234, 234, 0.5) !important;
	}
  
  	.age-bracket-label {
    	display: none !important;
    }
}
