css:
@-moz-document url-prefix("http://krissasaur.livejournal.com/feed/") {
.b-lenta-body H3,
.b-lenta-body H3 a {
    line-height: 1;
    color: #E9516D!important;
    font-family: consolas;
    font-size: 15px;
    font-weight: bold;
    letter-spacing: 2px;
    text-align: left;
    text-shadow: 1px 1px 1px #E9516D;
    text-transform: uppercase;
}
.meta-entryunit__items {
    list-style: none;
    color: #666666!important;
    font-family: calibri;
    font-size: 9px;
    letter-spacing: 0;
    margin: 0 0 0 170px;
    padding: 10px 10px 0 0;
    text-align: left;
    text-transform: uppercase;
}
.entryunit__head {
    margin: 0 0 0px 170px;
}
.entryunit__userpic {
    float: left;
    border-radius: 3px;
    overflow: hidden;
    margin: 32px 23px;
    background: none repeat scroll 0 0 #FFFFFF !important;
    padding: 10px;
    position: absolute;
    text-align: center !important;
    z-index: 15;
}
.types-entryunit {
    display: inline-block;
    margin: 0 0 10px 0px;
    padding: 0;
}
.flaticon--security-protected {
    width: 14px;
    height: 18px;
    background-position: -162px -75px;
    fill: #7A9199;
}
.js-font-family {
    font-family: consolas !important;
    text-align: justify;
    color: #888888;
    background-color: #FFFFFF;
    border-color: #EEEEEE;
    border-style: solid;
    border-width: 0;
    text-shadow: 1px 1px 2px #E0E0E0;
    font-size: 11px;
    line-height: 20px;
    margin: 0 0 0 auto;
    min-height: 83px;
    padding: 20px;
    width: 640px;
}
.js-font-family a {
    border-bottom: 1px dashed #16CCD5;
    color: #0FDCE6;
    font-family: sylfaen;
    letter-spacing: 2px;
    text-shadow: 1px 1px 1px #16CCD5;
    text-transform: uppercase;
}
.js-entry-bgcolor,
.ljcut-link:after,
.ljcut-pseudolink:after {
    background: #F7F7F7 url("http://i.imgur.com/KYxE6ff.png") repeat scroll 0px 0px;
}
.tagscurrents-entryunit {
    margin-top: 0;
    padding: 20px;
    list-style-type: none;
    font-size: .875em;
    background: #ffffff!important;
    width: 640px;
    margin: 0 0 0 auto;
}
.actions-entryunit {
    padding-left: 0;
    float: right;
}
}
