css:
/* i really want this to be global */
#XenForo > body {
    color: #F0F4C3;
}
#XenForo #content .pageContent {
    background-color: #313131;
}
#XenForo.messageList .message {
    background-color: #757575;
}
#XenForo .messageList .message a {
    color: lime;
}
#XenForo .bbCodeQuote .quoteContainer {
    background: #404040;
}
#XenForo a:link,
#XenForo a:visited {
    color: yellow;
}
#XenForo .PageNav a {
    background-color: #1B1B1B;
    color: white;
    background: black;
    border-bottom: 1px solid gold;
}
#XenForo .avatarHolder .userText,
#XenForo .extraUserInfo,
#XenForo .messageUserBlock,.messageUserBlock div.avatarHolder {
    background: black!important;
}
#XenForo .message .signature {
    background: #404040;
}
#XenForo .message .likesSummary {
    background-color: darkslategray;
}
#XenForo .featuredTopics, .pageContent center, .ptbbox{
display:none!important;
}
#XenForo .redactor_toolbar body{
color:black;
}
#XenForo .breadcrumb, .breadcrumb .crust a.crumb {
    background-color: black;
}

#XenForo .Menu .menuHeader {
    background: black;
}
#XenForo .Menu .secondaryContent {
    background-color: #444444;
}
#XenForo .Menu .sectionFooter {
    background-color: black;
}
#XenForo .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    background-color: darkorchid;
}
#XenForo .Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    background: darkorchid;
}

#XenForo .navPopup .PopupItemLinkActive:hover {
    background: darkslateblue;
}
#XenForo .messageList .message.staff {
    background-color: darkslategrey;
}
#XenForo .discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: #313131;
}
#XenForo .sectionMain {
    background-color: #404040;
}
#XenForo .sidebar * {
    background: black;
}
#XenForo .nodeList .categoryStrip {
    color: lightyellow;
    background-color: black!important;
}
#XenForo .node .nodeLastPost {
    background-color: #0C0C0C;
}
#XenForo .bbCodeQuote .attribution {
    color: yellow;
    background: #151515;
}

#XenForo .messageList .message.staff .signature {
    background: #183535;
}
#header {
    background: black;
}
#XenForo .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: #292929;
}
#XenForo .discussionListItem {
    background-color: #292929;
}
#XenForo .hasJs .bbCodeSpoilerText {
    display: none;
    background-color: inherit;
}
#XenForo .redactor_dropdown {
    background-color: #333333;
}
#XenForo li.message {
    background: #464646;
}
