css:
@-moz-document domain("www.dpstream.net"){ 
body {

background-image: url(https://image.noelshack.com/fichiers/2016/21/1464098866-bvs.png)!important;
background-attachment:fixed;
}

/*facebook et twitter pub et Général*/
div.moduleSection.subsection.facebook, div.moduleSection.subsection.twitter, div.moduleSection.subsection.general {
     display:none;
}

/*couleur écriture home page*/
.moduleSection .moduleTitle  {
color: white;}

.moduleSection .moduleHeader {
border-bottom : 1px solid white;}

.nav-pills>li>a, .nav-pills>li.active>a, .nav-pills>li.active>a:focus, .nav-pills>li.active>a:hover{
color : grey; }

.nav-pills>li>h2>a {
color: white; }

/*bouton envoyer*/

.btn-dp-fill-black {
background-color : transparent;
border: 2px solid #F1AA22;
}

/*couleur pseudo et bouton "répondre"*/
.smg-row .ans-link a {
color: white;}

/*News*/
div.tickercontainer, div#news_span {
display: none; 
}




/*démarrer visite*/
li#startTour {
     display: none; 
}
/*rendre la barre de recherche transparent*/
input#search-box {
     background-color: transparent !important;
     box-shadow: inset 1px 1px 2px #fcf8e3;
}

/*l'intérieur de l'endroit ou on écrit les coms*/
textarea {
     background-color: transparent !important;
     color: white !important;
}
/*barre profil paylist liens favoris*/
div.account-tabs {
     background-color: rgba(221, 221, 221, 0.12) 
}
.account-tabs ul li a {
     color: rgba(245, 245, 245, 0.72);
}
/*commentaires du profil*/
.a-coomentbox:nth-child(2n+2), .a-coomentbox:nth-child(2n+1) {
background-color: transparent !important;
color: grey;
border: 1px solid grey; 
}
/*box sous le textarea du tchat*/
div.description {
     display:none; 
}

/* dp logo et truc gris inutile et moche*/
.header, .navbar , .footer {
     background-color: transparent !important; 
}
.dp-black{
     box-shadow : none ;  
}

/*accueil affiche*/
#top.home { 
     background-image: none; 
}
/*type de film*/
select#MovieType.form-control.custom-select {
     background-color: transparent !important;
     box-shadow: inset 1px 1px 2px #fcf8e3;
     color: grey !important;
}

/*tchat*/
div.smg-row {
     background : none !important; 
     color: #ccc; 
}
/*fond des numerotation des page*/
nav.pageNavigation .pagination>li, nav.pageNavigation .pagination>li>a { 
     background-color: transparent !important; 
}
/*répondre*/
.md-modal .modal-content { 
     background-color: transparent !important; 
     color: white !important; 
}
.form-control {
     color: white !important; 
}
/*section commentaire*/
div.panel.panel-default {
     background-color: transparent !important; 
}
/*voir*/
a.showMoreLink {
     background-color: transparent !important; 
}
.showMoreErea:before {
     display: none; 
}
/*saison*/
div.panel {
     background-color: rgba(63, 63, 65, 0.53)!important;
     }
episode_number_right.default {
     color: white 
}
/*connexion*/
div.loginForm  {
     background-color: rgba(63, 63, 65, 0.53)!important;
}
input#userName, input#userPassword {
      background-color: transparent !important;
     box-shadow: inset 1px 1px 2px #fcf8e3;
}
/*barre info générale*/
ul#episodepageNav.nav-tabs, .episodepageNav, a#info-generales-tab {
     background: transparent !important;
}
/*rechercher par, catégories etc*/
div.container {
     background-color: transparent !important; 
     box-shadow : none;
}
div#select2-drop {
     background-color: rgba(0,0,0,0.79) !important; 
     box-shadow: inset 1px 1px 2px #fcf8e3;
     color: white !important;
     border-color: white;  }

input.select2-input, input.select2-input.select2-focused.select2-active {
     background : rgba(244, 244, 244, 0); 
     box-shadow: inset 1px 1px 2px #fcf8e3;
     color : white !important; }

.select2-results .select2-no-results, .select2-results .select2-searching, .select2-results .select2-selection-limit {
     background-color : transparent !important; } 

/*playlist mon profil*/
thead.cf {
     color : white !important ;}

.table-striped>tbody>tr:nth-of-type(odd), tbody {
     background-color : transparent !important ; 
     color : rgba(238, 238, 238, 0.7) !important ; } 

select#playlist_time {
     background-color: transparent !important;
     box-shadow: inset 0.6px 0.6px 0.8px #fcf8e3;
     color: grey  !important;
}
} 

/*TM*/

@-moz-document url("http://dpstream.net/tchats/tchat"), url("http://www.dpstream.net/tchats/tchat"), url-prefix("http://www.dpstream.net/tchats/tchat"), url-prefix("http://dpstream.net/tchats/tchat"){
section#top.inside {
display : none ; }
}

/*Modification du Forum*/

@-moz-document url("https://dpstream.net/forum/"), url("https://www.dpstream.net/forum/"), url-prefix("https://www.dpstream.net/forum/"), url-prefix("https://dpstream.net/forum/"){

html, body {
background-image : none ; 
}
   
div#wrap , div { 
     background-color: transparent !important; 
     border: none;
}
     
.navbar {
     border: 1px solid grey;
     background : none;
}

input#search_keywords.inputbox.search.tiny {
     background-color: transparent !important;
     color: white;
     border: 1px solid grey;
     }

blockquote {
     background-color: transparent !important;
     border-color: grey; }

.bg1, .bg2, .forumbg {
     border: 1px solid grey;}

.postprofile { 
     border: 1px solid grey}

.forumbg, .forabg {
     background-image: none;}

input#username.inputbox, input#password.inputbox  {
     background-color: transparent !important;
     border: 1px solid grey; 
     color : white ;
}
li.row, div.forabg{
     background-color: transparent !important;
     border: 1px solid grey; 
     border-bottom: none;
}
div.inner {
     background-color: transparent !important; } 

ul.topiclist dd {
     border-left-color: grey; }

ul.forums {
     background-color: transparent !important;
} 
}
