css:
@-moz-document domain("inbox.google.com") {
/* Hope you like the style! If you for some reason "out of the blue", want to contact me, you can find me on luvis.se
Have a nice day! */

/* section header "day -title" */

.ai-b8-bQ {
    background: transparent;
    margin-right: 48px;
    -webkit-flex: 1;
}
.section-header {
    color: #EAEAEA;
}
/* side bar */

.lQ {
    background: #4B4F56;
    bottom: 0;
    color: #ECEAEA;
    line-height: 40px;
    overflow-x: hidden;
    overflow-y: hidden;
    padding: 12px 8px 52px;
    position: absolute;
    white-space: nowrap;
    width: 216px;
    top: 0;
    text-rendering: geometricPrecision;
}
/* main settings */

body {
    font-family: 'arial', sans-serif;
    font-size: 83%;
    margin: 0;
    min-width: 400px;
    overflow-x: hidden;
    overflow-y: scroll;
    background: #27292B;
    color: #EAEAEA;
    text-rendering: geometricPrecision;

}

.I {
    background-color: #27292B;
}
.b2 {
    background-color: #27292B;
}


/* top bar */

.hn:before {
    background: #2B2D2F;
}

.b4, .b4 .at {
    background: #2B2D2F!important;
}
.hn:before {
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 -1px 4px rgba(0, 0, 0, 0.28);
}
.b4 .at::before {
    box-shadow: none;
}

.gH {
    background-color: #4B4F56;
}

/* main email list */

.hY, .bg, .g6 {
    color: #8D939A
}

.lT,
.r6,
.tQ{
color: #EAEAEA;
}

.scroll-list-item-highlighted>.an:not(.yS)::after, .scroll-list-item-highlighted>.an.D3tecb::after, .scroll-list-item-highlighted>.yS:not(.D3tecb) .hM::after, .scroll-list-item-highlighted>.yS:not(.D3tecb) .G4::after {
    background: gray;
}

.h8 {
    color: #EAEAEA;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
.an {
    background: #4B4F56;
    line-height: 20px;
    padding: 6px 0;
    position: relative;
}
.aa::before,
.an::before {
    bottom: 0;
    box-shadow: none;
    content: '';
    display: block;
    left: 0;
    pointer-events: none;
    position: absolute;
    right: 0px;
    top: 0;
}
.g6, .lo {
    color: #8D939A!important;
}

.rw {
    overflow: hidden;
    white-space: nowrap;
    color: #EAEAEA;
    font-weight: lighter;
}

.ss,
.qG {
    color: #FFFFFF;
    font-weight: bold;
}
.kj {
    background: transparent;
    color: #00acc1;
}
.le {
    color: #EAEAEA;
}
.qi {
    color: #8D939A;
}
.bO {
    color: #27292B;
}
/* active email */

.j6 {
    color: #EAEAEA;
}
.b5 {
    color: #4B4F56;
}
.aV {
    color: #4B4F56;
}

.X,
.ak,
.aa {
    background: #fff;
}
.ga.oY {
    background-color: #27292B;
}
.bc {
    background-color: #27292B;
    border-top: 0px;
    box-sizing: border-box;
    cursor: text;
    padding: 16px 20px;
    position: relative;
    width: 100%;
    z-index: 2;
}
.ac-P .bJ,
.ac-P-bh .bJ {
    background: #4B4F56;
    color: #fff;
}
.bJ {
    color: #27292B;
}
.dR {
    color: #27292B;
}

.az {
    background-color: #2F3133;
    padding: 20px 16px;
    color: #2B2D2F;
}

.section-header {
    color: #fff;
}

.az .ai-b8-bQ {
    background: transparent;
    padding-left: 8px;
    padding-bottom: 3px;
    padding-top: 3px;
    color: #8D939A;
    font-size: small;
    text-rendering: geometricPrecision;
}
.js{
    text-rendering: geometricPrecision;
}
}
