css:
/* i really want this to be global */

a {
	color: white;
}

.top_bar {
	background: #1d1d1d;
}

.main-wrapper {
	background: #222222;
}

.whats-going .recent-title {
	color: rgb(126, 126, 126);
}

.latest-videos h2 span {
	color: rgb(126, 126, 126);
}

.side-blocks h2 span {
	color: rgb(126, 126, 126);
}

.topic-in h3 {
	background: #1A1A1A;
}

.topic h2 {
	color: rgb(126, 126, 126);
}

a {
    color: #bfbfbf;
}

.topic-in h4 {
	background: #151212;
