    border-bottom: solid 1px #424242;
}

.topic-in {
	background: #1B1919;
}

.topic-in ul li a {
	color: #888;
}

.topic-in ul {
	border-right: solid 1px #424242;
}

.topic-in h3 {
    border-bottom: solid 1px #424242;
}

.tabs-nav ul li a {
    color: #888;
}

.game-one {
    background: #424242;
}

.game-one-rht p {
	color: #888;
}

.tabs-cont {
    border-bottom: solid 2px #424242;
}

.member-left-mdl {
    background: #333;
}

.member-left-top {
	background: #2e2e2e;
}

.member-left-top h2 {
	color: rgba(0,0,0,0.5);
}

.ui-widget-content {
	background: #252525;
}

.wall-tools, .plain-box {
    background-color: #292929;
}

.red, .fa.fa-ban {
    color: #1663C6;
}

.buy-premium-bar a, .buy-premium-bar a:link, .buy-premium-bar a:visited, .buy-premium-bar a:active, .buy-premium-bar span {
    color: #aaa;
    background: #424242;
}

.ui-progressbar.ui-widget-content {
    border: 1px solid #222; 
}

.ui-widget-header {
	background: #1FB56A; 
}

.member-left-mdl p {
	color: #bfbfbf;
}

.member-left .wall-textarea {
	background-color: #212020;
}

.wall-textarea:hover, .wall-textarea:focus {
        border: 1px solid #353333;
}

.status-time {
    border-bottom: 1px solid #252525;
}

.open-table {
	background-color: #2e2e2e;
}

strong {
	color: rgb(126,126,126);
}

input, button {
    background-color: #2e2e2e;
    border: #252525 1px solid;
}

.image-pending {
    color: #aaa;
    background-color: rgb(72, 72, 72);
}

.image-info {
    border: solid 1px #535354;
    background-color: #424242;
}

button:hover, button.active, INPUT[type="submit"]:hover, INPUT[type="reset"]:hover, INPUT[type="button"]:hover {
    background-color: #1CD76D;
    border: #333 1px solid;
}

#premium .cta {
	background-color: #333;
}

#premium .grpo {
    background-color: #424242;
    color: #8d8d8e;
}

#premium .grpe {
    background-color: #333;
    color: #8d8d8e;
}

td.row1, .row1 {
    background-color: #424242;
}

#premium .reas img {
    border: 1px solid #2e2e2e;
}

.css-tabs .ui-tabs-nav .ui-state-active a {
    color: #292929;
}

textarea {
    border: 1px solid #252525;
    color: #bbb;
    background-color: #2e2e2e;
}

textarea:hover {
    border: #424242 1px solid;
}

.general ul li.author {
	background: #424242;
    color: #aaa;
}

.general ul li.sticky h3 {
    background: #1A1A1A;
}

h3 {
    color: #8d8d8e;
}

.general ul li.forum-row, .general ul li.author, .general ul li.sticky, .general ul li.sticky-row, .general ul li.data-row {
    background: #2B2B2B;
    border-bottom: solid 1px #333;
}

.whats-going ul li {
    border-bottom: solid 1px #2e2e2e;
}

.ui-tabs .ui-tabs-nav li.ui-tabs-selected a, .ui-tabs .ui-tabs-nav li.ui-state-disabled a, .ui-tabs .ui-tabs-nav li.ui-state-processing a {
    background: #147BDB;
}

.tab-sec2-nav {
        border-bottom: solid 5px #147BDB;
}

.member-left-top ul.user-menu {
        background: #bbb;
}

.site-search .site-search-input {
	background: #525151;
}

.pages, .pagenum, .pagenumlast, .pagecurrent {
        background-color: #333;
        border: 1px solid #2e2e2e;
}

.post-row-middle {
    background: #333;
}

.user-details {
        color: #8d8d8e;
}

.post-row-bottom {
    background: #3A3A3E;
}

.post-row-bottom .posting_area {
    color: #ccc;
}

.like-sec {
    border-top: solid 1px #2E2E2E;
}

.friends, .medals, .user-links, .profile-links {
    background: #292929;
}

.friends h2, .medals h2, .user-links h2, .profile-links h2 {
    color: #999;
}

#chatwindow, #userwindow {
    background-color: #333;
}

#chatwindow .new {
    background: #12B75D url('http://www.thetechgame.com/images/new.png') no-repeat 99% 0;
}

.messagewrapper {
        border: 1px solid #676767;
}
