css:
@-moz-document domain("www.youtube-mp3.org") {
.success {
    background: #333;
    border: solid 1px #5CC7E4;
}
body {
    background:#333;
}
#form #youtube-url {
    color: #54A4A9;
    font-family: Arial;
    font-size: 16px;
    padding: 1%;
    width: 97.995%;
    border-color: #249496;
    border-style: solid;
    border-width: 1px;
    margin: 0;
    background: #531777;
}
#form #submit {
    padding: 5px;
    font-weight: bold;
    font-family: Arial;
    font-size: 14px;
    background: #5CA9C7;
    border: solid 1px #101156;
}
#description h2 {
    font-size: 13px;
    color: rgb(84, 164, 169);
    text-align: left;
    background: #531777;
    border-top: solid 1px #5573D3;
    padding: 3px 5px;
}
#sad {
    float: left;
    width: 160px;
    padding: 5px;
    background: #5ca9c7;
    border-left: solid 1px #0B172F;
    border-top: solid 1px #0B172F;
    margin-left: 15px;
    text-align: left;
}
#rad {
    margin-top: 43px;
    text-align: center;
    padding: 5px 0;
    background: #2E4E61;
    border-top: solid 1px #4D4575;
}
#description {
    margin-top: 25px;
    color: #2689A9;
    font-size: 13px;
}
#footer a {
    color: #842EE0;
    text-decoration: none;
    display: inline-block;
}
#footer a:visited {
    color: #A132B4;
}
#status_text {
    font-weight: bold;
    font-size: 13px;
    margin-bottom: 6px;
    display: block;
    color: #4FB1C1;
}
a {
    color: #009BCC;
}
b {
    font-weight: bold;
    color: #44819E;
}
#title {
    display: none;
    color: #755AC7;
}
img{
    display:none;
}
h1 {
    background-image: url("http://orig04.deviantart.net/e148/f/2016/115/2/3/drawing_by_mcfluffywolf-da098wt.png");
    background-repeat: repeat-y;
}
#content h1 {
    text-align: left;
    padding: 30px 0;
    margin: 0;
}
}
