@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ilovepdf.com"), domain("l2-de.ilovepdf.com"), domain("www.iloveimg.com") {
  
  /* Main Page Services list */
  body.index .apphome {
    background-color: #363636;
  }
  
  /* Main Body */
  body {
    color: #CCCCCC;
  }
  
  .page-content-wrapper {
    background-color: #161616;
  }
  
  /* Top Navigation Bar */
  .header-inner {
    background-color: #282828;
  }
  
  /* Paid Software link on Top Right end */
  .btn-default.desktop-software {
    background-color: #282828;
  }
  
  /* mid Navigation Bar */
  #navigation-inner {
    background-color: #202020;
  }
  
  /* mid Navigation Bar buttons */
  .navbar-nav > li > a {
    background-color: #161616;
  }
  
  /* Menu for mid Navigation Bar button */
  .dropdown-menu {
    color: #CCCCCC;
    background-color: #161616;
  }
  
  .dropdown-menu li > a {
    color: #CCCCCC;
  }
  
  .dropdown-menu .active > a:hover, .dropdown-menu li > a:hover {
    background-color: #484848;
    color: #ddd;
  }
  
  /* Dropdown for Bar with Headings - 'Merge PDF', 'Split PDF', ... */
  #navigation-inner .dropdown-menu {
    background-color: #161616;
  }
  
  .dropdown-menu.mega li > a {
    color: #aaaaaa;
  }
  
  /* long bar above 'Select PDF File' button */
  hr {
    border-bottom-color: #363636;
    border-top-color: #363636;
  }
  
  /* Page bottom */
  .page-footer-fixed .footer {
    background-color: #202020;
  }
  
  /* Compress button */
  .footer-button {
    background-color: #202020;
  }
  
  /* Compressed file list */
  .listview .file {
    background-color: #202020;
  }
  
  .listview .file:hover {
    background-color: #363636;
  }
  
  /* Compression Level buttons */
  .btn-default {
    color: #CCCCCC;
    background-color: #202020;
  }
  
  .btn-default.active {
    color: #CCCCCC;
    background-color: #363636;
  }
  
  /******** Uploading/Compressing Temporary page ********/
  #uploadingStatus {
    background-color: #363636;
  }
  
  #wh {
    -webkit-filter: brightness(0.21); 
    filter: brightness(0.21);
  }
  
  /*** Ads ***/
  
  /* Before processing */
  div#ad {
    display: none;
  }
  
  /* After processing */
  div#ad_squared {
    display: none;
  }
  
  /****** After compression Page ******/
  
  /*** 'File available time' Pop-up Pane ***/
  .modal-content {
    background-color: #282828;
  }
  
  .modal-header {
    border-bottom-color: #363636;
  }
  
  .modal-footer {
    border-top-color: #363636;
  }
  
  #defaultCountdown span.countdown-section {
    color: #CCCCCC;
    background-image: none;
  }
  
  /*** Download button ***/
  .btn-xl.download {
    border-right-color: #202020;
  }
  
  #over {
    background-image: none;
  }
  
  /*** Other buttons e.g. Google Drive, Dropbox, etc. ***/
  .exbu.downloadb .btn.redlove, #over img {
    background-color: #e4312c; /* bkgd of Download button */
  }
  
  /*** Compression % Msg ***/
  .alert-info {
    background-color: #282828;
    border-color: #363636;
  }
  
  /*** Share and Donate ***/
  /* Donate */
  .donate-button {
    background-color: #282828;
  }
  
  /* Share */
  .sharing-buttons li a.share-g {
    background-color: #a8392b; /* #d34836; */
  }
  
  .sharing-buttons li a.share-t {
    background-color: #2c6bb2; /* #4099ff; */
  }
  
  .sharing-buttons li a.share-l {
    background-color: #00567f; /* #007bb6; */
  }
  
  /****** After Delete Page ******/
  
  /* Ads */  
  ins[class="adsbygoogle"] {
    display: none !important;
  }

  /****** Login Page ******/
  
  /* Header */
  .login .logo {
    background-color: #B3B3B3;
  }
  
  .login .content {
    background-color: black;
  }
}
