css:
@-moz-document domain("gitter.im") {
.room-list-item__icon img {
    background: transparent;
}
.chat-item__action {
    color: white;
}
.trpContentPanel,
.chat-item:not(.unread) {
    background-color: rgb(29, 29, 29) !important;
}
.right-toolbar.right-toolbar--collapsible {
    background: black;
    color: white;
}
.trpMoreBadge div {
    color: white;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: white !important;
}
#chat-container,
.chat-item__text {
    color: #aaa !important;
}
.js-chat-name {
    color: white !important;
}
.chat-header,
.chat-input {
    background-color: black;
}
a {
    color: white
}
.right-toolbar {
    background-color: black;
    color: #aaa;
}
.chat-input__text-area {
    background-color: #000;
    padding-left: 1em;
    box-shadow: 0px 0px 5px #444;
}
.chat-input__box {
    background-color: #000;
}
.menu {
    background-color: #1C1819 !important;
}
.menu-list__overflow-shadow {
    background: linear-gradient(to bottom, rgba(56, 52, 53, 0) 0, #162E34 100%);
}
.room-list-item--current-room {
    background-color: #6A797D;
}
.trpBtnWhite {
    background-color: #6A797D;
    border-bottom: none;
}
.chat-input__text-area {
    color: white;
}
.trpButtonMedium.trpBtnWhite.gtrCreateRoomButton:not(:hover) {
    color: white;
}
.room-list-item.room-list-item--current-room span {
    color: white;
}
.trpAddBadge.js-add-more div {
    color: white;
}
.popover.in {
    background: rgb(59, 59, 59) !important;
}
.popover.top .arrow:after {
    border-top-color: rgb(59, 59, 59) !important;
}
.popover.bottom .arrow:after {
    border-bottom-color: rgb(59, 59, 59) !important;
}
.popover.right .arrow:after {
    border-right-color: rgb(59, 59, 59) !important;
}
.popover.left .arrow:after {
    border-left-color: rgb(59, 59, 59) !important;
}
.popover-footer.trpBodySmall {
    background: rgb(59, 59, 59) !important;
}
.popover.in .popover-title {
    background: rgb(79, 79, 79) !important;
    color: white;
    border-color: white;
}
.popover-content .chat-item__text,
.popover-content .chat-item__from {
    color: white !important;
}
.chat-header__avatar {
    background-color: black;
}
.js-search-input.search-input-field {
    background: black;
    box-shadow: 0px 0px 5px #444;
    color: white;
}
.trpPopoverDetails h1,
.trpPopoverDetails h2,
.trpPopoverDetails .vcard-stats,
.trpPopoverDetails .trpUserDetails {
    color: white;
}
::-webkit-scrollbar {
    width: 12px !important;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3) !important;
    border-radius: 10px !important;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px !important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.5) !important;
    background: #668;
}
.chat-item__container--status {
    background: rgb(70, 70, 70) !important;
    border: 0;
}
.octicon-tools:before {
    color: white;
}
.trpAddBadge {
    color: white !important;
}
hr {
    border-top: 0;
    border-bottom: 1px solid rgb(59, 59, 59);
}
span[data-group-name="all"],
.unread,
.modal--default__header,
.modal--default__content,
.modal--default__footer,
body,
.popover-footer,
.banner-wrapper .banner .mention {
    background-color: rgb(29, 29, 29) !important;
}
.permission__details,
.markdown-help-content,
.modal--default__content {
    color: white !important;
}
.welcome-modal__collaborators,
.welcome-modal__collaborator,
.collaborators-container,
.userhome-main {
    background-color: rgb(29, 29, 29) !important;
    color: white !important;
}
.trpChatInput,
.dropdown,
div.result.selected,
.result:hover {
    background-color: black !important;
    color: white !important;
}
span.groupmention {
    background-color: transparent !important;
}
.menu-list__overflow-shadow {
    background: none;
}
.issue.open {
    background-color: #072c1a !important;
    color: #169c58 !important;
}
.issue.closed {
    background-color: #2e0905 !important;
    color: #9f2013 !important;
}
.commit {
    background-color: #1b0c27 !important;
    color: #5c2989 !important;
}
div.chat-connectivity-indicator {
    -webkit-filter: invert(100%) hue-rotate(180deg)
}
i.search__toggle-icon.octicon.octicon-search {
    color: #333;
}
.chat-header__title {
    color: #dbdbdb;
}
.dropdown {
    background-color: #353535;
}
.chat-header__settings {
    color: rgb(200, 200, 200);
}
.popover-footer {
    border-radius: 0 !important;
}
.trpBtnLightGrey {
    background-color: rgba(0, 0, 0, 0);
    color: #dbdbdb;
    box-sizing: content-box;
    margin-right: 15px;
}
#button-onetoone {
    background-color: rgba(0, 0, 0, 0);
    color: #1dce73;
}

#button-remove {
    background-color: rgba(0, 0, 0, 0);
    color: #e74c3c;
    margin-right: 15px;
}
ul.dropdown a {
    color: #dbdbdb;
}
ul.selectable li.active>a {
    border-radius: 0;
    background-color: #999;
}
}
