css:
@-moz-document domain("truesteamachievements.com") {
.navbar { border: 0px; }
.navheader { background-color: #171a21; padding-top: 14px; padding-bottom: 15px; }
body { background-color: #1b2838; }
#main { background-color: #16202d; }
#main-holder { background-color: #16202d; }
#page { background-color: #1b2838; }
#footer-wrap { background-color: #1b2838; border: 0px; }
#footer a { color: #67c1f5; }
.ih { background-color: #57cbde; border: 0px; }
#main div.itemright h3.status { background-color: #3f78a2; color: #67c1f5; }
#spnCurrentStatus, h3.status { color: #67c1f5; } 
#sidebar div.smallpanel { background-color: #16202d; }
a, .itemright a, .panelcontent a, .maintable a, .friendfeed a { color: white; } 
.smallpanel table td { border-bottom: #67c1f5 dotted 1px; }
div.friendfeeditem span.green { background-color: #3f78a2; }
div.friendfeeditem span.blue { background-color: #4c6b22; color: #a4d007; }
div.friendfeed h6 { background-color: #3f78a2; color: #67c1f5; }
div.friendfeeditem span.red { background-color: #3f78a2; }
div.friendfeeditem span { background-color: #16202d; }
tr.myrow td, tr.selected td { background-color: #4c6b22; color: #a4d007; }
table.maintable tr.myrow td, table.maintable tr.green td, table.maintable tr.selected td, table tr.green td { background-color: #4c6b22; color: #a4d007; }
#searchform #txtSearch { background-color: #5298c8; border: 1px solid #346384; color: #305d8a; }
div.badgepanel img { margin: 0px 5px 0px 0px; }
div.tabs { background-color: #3f78a2 }
div.tabs li.selected>span { color: #67c1f5; }
div.tabs li.selected>a { color: #67c1f5; }
div.tabs a:hover { color: #FFFFFF; }
div.tabs li.selected { background: none; }
div.tabs li a, div.tabs li>span { color: #67c1f5; }
