div.tabs .tab_community u, div.tabs .tab_pink u { color: inherit; }
div.tabs .tab_kinect u, div.tabs .tab_purple u { color: inherit; }
div.tabs .tab_xbla u, div.tabs .tab_orange u { color: inherit; }
