.menuitems li, .filterbutton li { background-color: #1b2838; }
.menuitems { background-color: #1b2838; border: 0px; }
ul.chartlist li { background-color: #3f78a2; border: 0px; }
div.friendfeeditem span.yellow { background-color: #1b2838; }
.calendarsmall { background: transparent; }
.news_community a { color: white; }
#main-wrap { background-color: transparent; }
h1.block, h2.block, h3.block, h4.block, h5.block, h6.block, .helppage h4 { color: white; background-color: rgba( 21, 31, 44, 0.7 ); }
div.friendfeeditem span.purple { background: transparent; }
div.news div.details, div.bloglist div.blogpost { background-color: #3f78a2; color: #67c1f5; border-bottom: 0px; padding: 10px; }
h1.block, h2.block, h3.block, h4.block, h5.block, h6.block, .helppage h4 { border-bottom: 0px; }
div.addedby { color: #67c1f5; }
#breadcrumbs { background-color: #3f78a2; }
#breadcrumbs ul a { color: #67c1f5; }
#breadcrumbs ul li { color: white; }
div.filter { background: transparent; }
#divGamerPeriodSummary .gamesummary { background: transparent; }
.informationpanel, div.information  { background-color: #3f78a2; border: 0px; color: white; }
h1.pagetitle, h2.pagetitle, h3.pagetitle, h4.pagetitle, .helppage h3 { color: white; border-bottom: 0px; }
.button { background-color: #3f78a2; background-image: none; border: 0px!important; border-radius: 0px; }
.button:hover { background-color: #3f78a2; background-image: none; border: 0px!important; border-radius: 0px; }
div.friendfeeditem .Comment p { background-color: #1b2838; }
div.friendfeeditem .Comment, #divAddComment { background-color: #1b2838; }
div.blogpost h5 { color: #67c1f5; }


/* Hover Menu */

#headermenu li:hover ul li.category { background-color: rgba( 21, 31, 44, 0.7 ); }
#headermenu li:hover a { background-color: #3f78a2; }
#headermenu li:hover { background-color: #3f78a2; }
#headermenu li:hover ul { background-color: #3f78a2; }
#headermenu li:hover ul li { background-color: #3f78a2; }

/* Forum Stuff */

table.maintable td.posted, table.maintable td.author, table.maintable td.voting, table.maintable td.subject { border-color: #1b2838; }
span.quote, .podcast_related {  background-color: #3f78a2!important; border:0px!important; }
.adv_editbox .nolabel .aeb_wrap, .adv_editbox .nolabel .toolbar { background-color: #3f78a2; }
#txtQuickReply { background-color: rgba(37,37,37,1); border: 0px; color: white; }
}

