div.panelpink h4 u, div.panelpink h4 a.name u { color: white; }
div.paneltabs a.selected { background-color: #3f78a2!important; border: 0px; }
div.paneltabs a, div.paneltabs a:visited { background-color: #16202d; border-color: #3f78a2; }
div.paneltabs { border-bottom: 1px solid #3f78a2; }
img.smallicon { margin-top: 5px; }
div.panelpurple h4 u, div.panelpurple h4 a.name u { color: inherit; }
div.panelpurple h4 { border-color: inherit; }
table.maintable tr.even td { background-color: #3f78a2; }
table.maintable tr.odd td { background-color: #3f78a2; }
}

@-moz-document url-prefix("https://truesteamachievements.com/gamer/") {
/* User Games Page and Achievements Page */

