css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("washingtonpost.com") {
  
  html, body, element {
    background-color: black;
    color: grey;
  }
.pb-f-article-article-body .article-body {
    color: #868686!important;
    text-shadow: 0 1px 0 #000;
}
  .top-sharebar-wrapper {
    padding: 5px;
    background-color: #101010!important;
    border-top: 1px solid #d5d5d5;
}
  sticky-top-sharebar .utility-tools-wrapper {
    background: #515151;
    border-top: 1px solid #030303!important;
    padding: 5px 0;
}
  .lb-persist-top-true .pb-f-ad-leaderboard .leaderboard {
    border-bottom: 1px solid rgba(213, 213, 213, 0) !important;
    background: rgba(0, 0, 0, 0) !important;
}
  .layout_article #main-content, .layout_article #article-content {
    padding-right: 8.33333333% !important;
    background-image: none!important;
    background-position: center center;
    background-repeat: repeat-y;
    background-position: 94%;
}
  .layout_article #right-rail ul {
    margin-left: 0;
    margin-top: 0;
    background: black!important;
}
.pb-f-page-post-most .post-most ul.large {
    list-style: none outside none;
}
.skin.skin-card {
    padding: 15px;
    border: 1px solid #151515!important;
    border-bottom-color: #2d2d2d!important;
    border-bottom-width: 2px;
    border-radius: 4px;
    background-color: #ffffff;
    color: #957676;
}
  .pb-f-article-article-topper h1 {
    font: normal 48px "PostoniStandardBold",Georgia,serif;
    line-height: 1.15em;
    color: #686868;
    margin-top: 0;
    margin-bottom: 0;
    display: inline-block;
}
  .echo_container, .echo-apps-conversations-container li > a, .echo-apps-conversations-container .echo-primaryFont, .echo-streamserver-controls-stream-item-plugin-CardUIShim .echo-streamserver-controls-stream-item-body .echo-streamserver-controls-stream-item-text, .echo-apps-conversations-container .echo-secondaryFont, .echo-streamserver-controls-stream-item-authorName, .echo-streamserver-controls-stream-item-plugin-CardUIShim .echo-streamserver-controls-stream-item-buttons, .echo-apps-conversations-streamingStateContainer, .btn.btn-primary.echo-streamserver-controls-submit-plugin-CardUIShim-button, .btn.echo-primaryFont.echo-streamserver-controls-submit-postButton, .echo-streamserver-controls-stream-more, .echo-sdk-ui .echo-apps-conversations-streamSorter .nav .dropdown .dropdown-toggle, .echo-apps-conversations-streamingState, .echo-canvas-container .echo-topcommenter-form .echo-close, .echo-topcommenter-form, .echo-topcommenter-form p {
    color: #9c9c9c!important;
}
  .pb-f-sharebars-top-share-bar .vertical-sticky-top-sharebar .utility-tools-wrapper .tool, .pb-f-sharebars-top-share-bar .vertical-sticky-top-sharebar .social-tools-wrapper .tool {
    padding: 16px 16px;
    border: none;
    background: #101010!important;
}
.pb-f-sharebars-top-share-bar .vertical-sticky-top-sharebar .utility-tools-wrapper .tool {
    color: #CEBABA;
}
  .pb-f-sharebars-top-share-bar .vertical-sticky-top-sharebar .utility-tools-wrapper {
    background: #000!important;
    border-top: 1px solid #383838!important;

}

.pb-bottom-author, .layout_article #pb-root .pb-feature.pb-f-page-recommended-strip .flex-item .headline a {
    color: #A1A2A1!important;
}
}
