css:
@-moz-document url-prefix("https://dev.detendusdupad.com/") {
*, *:before, *:after {
    margin:0px;
    padding:0px;
    -webkit-box-sizing:border-box; 
    -moz-box-sizing:border-box; 
    box-sizing:border-box;
    border:0px;
    font-size:100%;
    font:inherit;
    outline:0;
}

body {
	background: #DFF2FD;
}
#header,
#nav-topbar.nav-container {
	background: #1A8BB2;
}
#nav-topbar.nav-container {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}
.toggle-search {
	box-shadow: none;
}
#header {
	padding-bottom: 0;
}
#header .site-title {
	padding: 0;
}
#header .pad {
	padding-bottom: 0;
}
#page {
	margin-top: 20px;
	padding-bottom: 20px;
}
	
	#page .container-inner {
		background: none !important;
	}
	
	.main {
		background-color: transparent !important;	
	}
	
	.main .content {
		background: white;
		width: 98%;
	}
	
	.main .sidebar {
		background: transparent;
	}
	
.col-2cl .main-inner {
	/* background: url(https://hostr.co/file/4lL6jxBdTKl7/s-right-s1.png) right 0 repeat-y; */
	background: none;
}
	
	.col-2cl .s1 {
		
	}
	
	#nav-topbar .nav li a {
		color: white;
		background: linear-gradient(to bottom, transparent 50%, #0E5066 50%);
    background-size: 300% 100%;
    background-position: bottom;
    transition:all 0.2s linear;
	}
	
	#nav-topbar .nav > li:hover > a, #nav-topbar .nav > li > a:hover, .nav ul, .nav > li:hover > a, .nav > li > a:hover {
    background-position:bottom;
		background-color: transparent;
	}
	
/*** WIDGET SIDEBAR ***/
	.sidebar .widget h3 {
    color: #90A4AE;
    /* background: #CFD8DC; */
		width: 100%;
		text-align: center;
		padding-bottom: 15px;
		padding: 10px;
		border-bottom: 1px solid #CFD8DC;
	}
	
	.sidebar .widget {
		background: white;
		margin-bottom: 20px;
		padding: 0;
		border: 1px solid #CFD8DC;
	}
	
	@media only screen and (min-width: 480px) and (max-width: 960px) {
		.main .sidebar {
			background: white;
		}
		
		.sidebar .widget {
			border: 0;
		}
	}
	
	col-2cl.s1-expand .s1 .widget {
		border: none !important;
	}
	
	.sidebar .textwidget, .widget_discourse_latest ul, ul.alx-posts {
		padding: 10px;
	}
	
	.sidebar-content {
		margin: 0 !important;
	}
/****** END WIDGET SIDEBAR ******/
	
	
	
.sidebar-content {
	border-radius: 10px;
	margin-top: 20px;
	margin-bottom: 20px;
}
.sidebar {
	border-bottom-left-radius: 10px;
	border-bottom-right-radius: 10px;
	padding-bottom: 0;
}
.widget h3 {
	color: #004563;
	font-weight: bold;
	font-size: 1.1em;
	margin-bottom: 0;
}
.widget_discourse_latest div,
.widget_discourse_latest div.avatar,
.widget_discourse_latest ul li div:nth-child(2) {
	padding-bottom: 5px;
}
.widget_discourse_latest a.topic:hover {
	color: #004563;
}
.widget_discourse_latest .badge-category {
	opacity: 0.6;
}
/**** COURTS METRAGE WIDGET ***/

.thumb-icon {
	background: white;
	color: #41c0f0;
	transform: none;
	-webkit-transform: none;
	-moz-transform: none;
	-ms-transform: none;
	-o-transform: none;
	height: 50px;
	width: 50px;
	bottom: 10px;
	left: 10px;
	-webkit-transition-duration: 0.2s;
	-moz-transition-duration: 0.2s;
	transition-duration: 0.2s;
}
.thumb-icon i {
	transform: none;
	-webkit-transform: none;
	-moz-transform: none;
	-ms-transform: none;
	-o-transform: none;
}
.thumb-icon.small .fa {
	font-size: 2em;
	line-height: 1.3em;
}
.thumb-icon .fa-play {
	margin: 0;
}
.alx-posts li {
	border-bottom: 0;
}
.alx-posts img {
	border-radius: 5px;
}
#page .container-inner {
	box-shadow: none;
}
#footer-bottom {
	background: #191919;
}
#footer-bottom .social-links a {
	opacity: 0.2;
	transition: 0.2s all linear;
}
#footer-bottom .social-links a:hover {
	opacity: 1;
}
#footer-logo {
	opacity: 0.2;
}
	
/** END DOCUMENT URL PREFIX **/
}
