css:
@-moz-document domain("shizafm.com") {
body{
	background: url(https://pp.vk.me/c628618/v628618404/484e3/zkBwLuW4ZMM.jpg) no-repeat;
    background-size: cover;
    background-color: #141640;
}
.content-nav{
	background-color: rgba(0, 0, 0, 0.5);

}
.content-nav a{
  color: #00F8F9;
        border: none;
}
.plays a{
  color: #00F8F9;
        border: none;
}
.block ul{
    border: none;
}
.block{
	background-color: rgba(0, 0, 0, 0.5);
    border: none;
    color: white;
    text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
}
.track-list{
    border: 1px solid #00F8F9;
}
.player{
    border: 1px solid #00F8F9;
}
.shiza-chat-message-2{
	border: none;
}
.avatar-border{
	border-color: #00F8F9;
}
.shiza-chat-avatar{
	border: none;
}
.player-controls{
    border-right: none !important;
}
.track-list .plays {
    background: none;
}
.player .bitrates .bitrate {
    background-color: #00F8F9;
    font-size: 13px;
    text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
    color: white !important;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    padding: 4px 4px;
}
.jp-volume-bar-value{
    background-color: #00F8F9;
}
.header-logo{
	background: url(http://s50294aw.bget.ru/old_logo3.png) no-repeat center;
    background-size: 70% auto;
    background-position: bottom;
    opacity: 0.8;
}
.header-container{
	margin-left: -100px;
}
.age-warning{
	opacity: 0;
}
.alert{
	opacity: 0.1;
}
.shedule-list .day {
	background: none;
}
.shedule-list li{
	border-bottom: 1px solid #00F8F9;
}
.chat-controls {
    border-top: 1px solid #00F8F9;
    background-color: rgba(0, 0, 0, 0);
    height: 55px;
}
.shiza-chat-container textarea {
    height: 54px;
    width: 736px;
    font-size: 14px;
    margin: 15px 0px 1px 25px;
    border: 1px solid #00F8F9;
    background-color: rgba(0, 0, 0, 0);
        color: white;
    text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
    box-shadow: 0 0 10px #00F8F9;
    margin-bottom: 15px;
}
.shiza-chat-container button{
	background-color: rgba(0, 0, 0, 0.2);
    cursor: pointer;
            color: white;
    text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
    box-shadow: 0 0 10px #00F8F9;
}
.shiza-chat-container button:hover {
background-color: rgba(0, 0, 0, 0.7);
}
.label{
	background-color: rgba(214, 27 , 220, 0.6) !important;
}
.strannik{
	opacity: 0.5;
}
.money-pic {
    background: url(http://shizafm.com/assets/img/money_dark.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    opacity: 0.7;
}
select {
   	background-color: rgba(0, 0 , 0, 0.2) !important;
    border: 1px solid rgba(200, 165, 165, .2);
    color: #777;
}
tr {
   background-color: rgba(0, 0, 0, 0.2);
   color: white;
   text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
}
.search{
    border: 1px solid #00F8F9;
    background-color: rgba(0, 0, 0, 0.4);
        color: white;
    text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
    box-shadow: 0 0 10px #00F8F9;
    padding: 5px;
}
.search_button{
	 background-color: rgba(0, 0, 0, 0.7);
    cursor: pointer;
     color: #00F8F9;
       text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
}
.search_button:hover {
	 background-color: rgba(0, 0, 0, 0.9);
    cursor: pointer;
     color: #00F8F9;
       text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
}
#shedule{
        cursor: pointer;
     color: #00F8F9;
       text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
    background-color: rgba(0, 0, 0, 0.6);
}
#shedule a{
        cursor: pointer;
     color: #00F8F9;
       text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
}

.shiza-chat-text p span.hashtag {
    background-color: rgba(214, 27 , 220, 0.6) !important;
    border-radius: 3px;
    padding: 5px 7px !important;
}
.player-controls .icon, .track-list .plays {
    opacity: 1 !important;
}
.player .bitrates .bitrate {
    opacity: 1;
}
.content-nav{
	border: 1px solid #00F8F9;
}
.widget, .applets{
    border: 1px solid #00F8F9;
}
.questions {
    background-color: rgba(0, 0, 0, 0.2);
   border: 1px solid #00F8F9;
}
.question {
    background-color: rgba(0, 0, 0, 0);
}
.shiza-chat-text .icon.remove {
    color: white;
    font-size: 22px;
           text-shadow: 1px 1px 2px #00F8F9, 0 0 1em #00F8F9;
}

#stickers_menu{
	    background-color: rgba(0, 0, 0, 0.3);
}
#stickers_menu li {
    background-color: rgba(0, 0, 0, 0.3);
    box-shadow: none;
}
}
