css:
@-moz-document url-prefix("http://store.steampowered.com/app/"), url-prefix("https://store.steampowered.com/app/") {
#game_area_reviews {display: none !important;}
#recommended_block {display: none !important;}
#app_reviews_hash {display: none !important;}
#game_area_legal {display: none !important;}
.queue_overflow_ctn {display: none !important;}
.early_access_header {display: none !important;}
.early_access_banner {display: none !important;}
.early_access_announcements {display: none !important;}
.sys_req {display: none !important;}
.glance_ctn_responsive_left {display: none !important;}
.glance_ctn_responsive_right {display: none !important;}
.responsive_apppage_details_right {display: none !important;}
.responsive_apppage_details_left:not(#category_block) {display: none !important;}
.responsive_apppage_reviewblock {display: none !important;}
.game_area_play_stats {margin:0 auto;}
.game_area_dlc_section {margin-top:0;}
.game_area_description {margin-top:0;}
#recommendation_success {display:none;} 
.game_page_autocollapse { max-height: none!important }
.game_page_autocollapse_fade { display: none!important }
}
