@-moz-document domain("chatango.com") {
/* 
* ------------ CSS FOR FLASH PRIVATE MESSAGES CHAT ------------ *Credit to Viiencoding
*/

body{
    overflow-x:hidden
}

#ad_wrapper {
	position: absolute;
	text-align: right;
	top: 0px;
	right: 0px;
	height: 0px;
	width: 0px;
}

#ad {
	float: right;
	clear: both;
	padding: 0 5px;
    width: 0px;
    height: 0px;
}

#full_height_td {
 min-width: 510px;
 height:100%;
	width: 100%;
 vertical-align:top;
 position: relative;
}
	
.bottombar{
	width: 0px;
	height: 0px;
	text-align: center;
}


/* 
* ------------ CSS FOR HTML/JS CHATROOMS ------------
*/

.thumb-popup{
    text-align:left !important;
}


/* 
* ------------ Anti Flood ------------ * Credit to Shikaku2 for the idea
*/

.msg {
    display:block;
	overflow-y: hidden;
	max-height: 200px;
}



/* 
* ------------ v Removes Stuff like Underlined, Italics and Bold v  ------------
*/


#OM .msg u,
#OM .msg b,
#OM .msg i,
#OM .msg span:nth-child(1) {
    text-decoration: none !important;
    font-style: normal !important;
    font-weight: normal !important;
}


/* 
* ------------ ^ Removes Stuff like Underlined, Italics and Bold ^ ------------
*/



/* 
* ------------ v Capitalizes the first letter of each message v -- FAULTY --  Its worth noting this will mess with emoticons
*/

#OM .msg u:first-letter,
#OM .msg b:first-letter,
#OM .msg i:first-letter,
#OM .msg span:first-letter,
span:first-letter,
#OM .msg p:first-letter {
    text-transform: capitalize !important;
}

/* 
* ------------ ^ Capitalizes the first letter of each message ^ -- FAULTY --  Its worth noting this will mess with emoticons
*/

/* 
* ------------ v Capitalizes the first letter of peoples account names and makes them bold v ------------ 
*/

#OM .c_username {
    text-transform: capitalize;
    font-weight: bold !important;
}

/* 
* ------------ ^ Capitalizes the first letter of peoples account names and makes them bold ^ ------------ 
*/

/* 
* ------------ v Removes the little white box that filtered messages appears as v ------------ 
*/

#OM .filtered {
    display: none;
}

/* 
* ------------ ^ Removes the little white box that filtered messages appears as ^ ------------ 
*/

/* 
* ------------ v Sets a standard for messages and their size. v ------------ 
*/

#OM .msg {
    font-size: 12px; /* Change 12px to 10px or 20px to make the font size smaller or bigger */
    padding: 8px 4px 8px 4px !important;
    border: 0 !important;
}

/* 
* ------------ ^ Sets a standard for messages and their size. ^ ------------ 
*/

/* 
* ------------ v Pushes in messages and makes thumbs smaller. v ------------ 
*/

#OM .msg .user-thumb {
    display: block !important;
    width: 45px !important;
    height: 45px !important;
    margin-right: 10px !important;
    border-radius: 45px !important;
}

/* 
* ------------ ^ Pushes in messages and makes thumbs smaller. ^ ------------ 
*/

#OM .msg-stripes{
	margin-left:-4px;
    padding-right:2px;
}

/* 
* ------------ v Changes all fonts to a set font. v ------------ 
*/

#OM .msg p span,
#OM .msg p,
#GBG #input-field {
    font-family: Rawline, sans-serif !important; /* Change "Rawline" to your preffered font, if multiple words enclose the font name in '' Likeso: 'font name' */
    font-size: 11px !important; /* Change 12px to 10px or 20px to make the font size smaller or bigger */
    font-weight:400 !important;
}

/* 
* ------------ ^ Changes all fonts to a set font. ^ ------------ 
*/



/* 
* ------------ v Changes Pings / Mentions / @ to a set font. v ------------ 
*/



#OM .msg span .at-reply {
    display: inline-block !important;
    font-weight: 600 !important;
    font-family: Rawline !important;
}

/* 
* ------------ ^ Changes Pings / Mentions / @ to a set font. ^ ------------ 
*/


#OM .msg-stripes{
	margin-top:-8px;
}

/* 
* ------------ v Places account names on a line for itself. v ------------ 
*/

#OM .msg .c_username {
    display: block !important;
    margin: 0 5px 5px 0 !important;
    z-index: 1 !important;
    pointer-events: none !important;
    font-weight: 400 !important;
}

/* 
* ------------ ^ Places account names on a line for itself. ^ ------------ 
*/



/* 
* ------------ v Changes Dates to a set font color v ------------ 
*/

#OM .msg .msg-date {
    float: right !important;
    clear: none !important;
    color: #bbb !important;
}

/* 
* ------------ ^ Changes Dates to a set font ^ ------------ 
*/

/* 
* ------------ v Mod Badges Pushed v ------------ 
*/

#OM .msg .msg_badge {
    float: left !important;
    margin-right: 4px !important;
}

/* 
* ------------ v Removes yellow background on @ quotes v ------------ 
*/

#OM .msg .at-quote {
    background: none !important;
}

/* 
* ------------ v Removes IPs for mods v ------------ 
*/
#OM .msg .msg-ip {
    display: none;
}

/* 
* ------------ v Makes links blue v ------------ 
*/

/* 
* ------------ v Ads and Media v ------------ 
*/

#group_table tr:nth-child(1),
#group_table tr:nth-child(2),
#group_table tr:nth-child(3) {
    display: none !important;
}

#GTL,
#UTL {
    display: none !important;
}

/* 
* ------------ ^ Ads and Media ^ ------------ 
*/

/* 
* ------------ v Input field & Below styling v ------------ * Credit to cowwwwww
*/

#IW{
    margin-top:5px;
}

#IWW,
#IWER2,
#input-field {
    height: 28px !important;
    max-height: 28px !important;
    padding: 0 !important;
    color: #eee;
    background: #fff;
}

#input-field {
    box-sizing: border-box !important;
    padding: 6px 6px 0px 6px !important;
    color: #EEE;
    border: 1px solid #f5f5f5 !important;
}

.msg.ubdr {
    border: 0 !important;
}
.bottombar.has-border {
    display: none !important;
}

