css:
@-moz-document domain("kunskapsporten.se") {

.kp-bootstrap-335 .navbar-ks {

    background: #27318b;

    background: -moz-linear-gradient(top, #27318b 0%, #242d7c 100%);

    background: -wevbkit-gradient(linear, left top, left bottom, color-stop(0%, #27318b), color-stop(100%, #242d7c));

    background: -webkit-linear-gradient(top, #27318b 0%, #242d7c 100%);

    background: -o-linear-gradient(top, #27318b 0%, #242d7c 100%);

    background: -ms-linear-gradient(top, #27318b 0%, #242d7c 100%);

    background: linear-gradient(to bottom, rgb(0, 26, 255) 0%, #0718AF 100%);

    border-radius: 0px;

    border: none;

    margin-bottom: 0px;

}

body {

    margin: 0;

    font-family: "open sans", sans-serif;

    font-size: 16px;

    line-height: 1.7em;

    letter-spacing: normal;

    color: #848282;

    padding-top: 10px;

    background-color: rgba(112, 175, 200, 1);

    background: url(http://www.freedomwallpaper.com/nature-wallpaper/nature-hd-wallpapers-water.jpg);

    -webkit-background-size: cover;

    -moz-background-size: cover;

    -o-background-size: cover;

    background-size: cover;

    background-attachment: fixed;

    -webkit-font-smoothing: antialiased;

}

}
