css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gleerupsportal.se") {
  .gbook_product_title {
    height: 40px;
    line-height: 40px;
  }
  
  .gleerups_app_header, .gleerups_portal_header {
    z-index: 10000;
  }
  
  .gleerups_portal_header {
    height: 10px;
    overflow: hidden;
    background-color: #222;
    transition: height 0.6s, background-color 0.6s;
  }
  .gleerups_portal_header:hover {
    height: 64px;
    background-color: #FFF;
    transition: height 0.2s, background-color 0.2s;
  }
  
  .gleerups_app_header {
    height: 40px;
    line-height: 40px;
  }
  .gleerups_right_menu, .gleerups_left_menu {
    height: 40px;
  }
  .gleerups_menu_item {
    height: 40px;
    line-height: 40px;
    vertical-align: top;
  }
  .gbook_addons_and_notes {
    height: 40px;
    line-height: 40px;
  }
  .table_of_contents, .search {
    top: 40px;
  }
  
  .gleerups_fixed_scrollingcontentwrapper {
    top: 50px;
  }
  .content {
    overflow: hidden;
  }
}
