css:
@-moz-document domain("gateway.sdl.com") {

/** Global styles *********************************************************************************************************************************************/

body {
     font-family: Myriad Pro Light, Arial;
     background: #C7C7CC;
     /* For browsers that do not support gradients */
     
     background: -webkit-linear-gradient(#F7F7F7, #C7C7CC);
     /* For Safari 5.1 to 6.0 */
     
     background: -o-linear-gradient(#F7F7F7, #C7C7CC);
     /* For Opera 11.1 to 12.0 */
     
     background: -moz-linear-gradient(#F7F7F7, #C7C7CC);
     /* For Firefox 3.6 to 15 */
     
     background: linear-gradient(#F7F7F7, #C7C7CC);
     /* Standard syntax */
}
.sdl-res-body-color {
     background-color: transparent;
     border: 0px;
}
@media (min-width: 768px) {
     .nav-tabs.nav-justified>li>a {
          border-bottom: 0px solid #ddd;
          border-radius: 0px 0px 0 0;
          color: #000;
     }
