	 * Override Bootstrap's default container.
	 */
}
@media (min-width: 1500px) {
     .container {
          width: 1100px;
          border: 0px transparent;
     }
}
.container {
     position: relative;
}
.dropdown-backdrop {
     position: static;
}
.image-container {
     text-align: center;
}
.sdl-res-pad10px {
     margin: 3px;
}
.sdl-res-spacer10px {
     margin-top: 20px;
}
.sdl-res-spacer20px {
     margin-top: 20px;
}
.sdl-res-logo-margin {
     margin-left: 0px;
     margin-top: 0px;
     margin-bottom: 0px;
}
.sdl-res-unread {
     background-color: transparent;
}
.sdl-res-selectedBackground {
     background: transparent;
}
.sdl-res-voting-background {
     background: transparent;
}
/** Header and navigation styles *********************************************************************************************************************************************/

.navbar-default {
     background-color: transparent;
     border-color: transparent;
}
.btn-group-vertical>.btn-group:after,
.btn-toolbar:after,
.clearfix:after,
.container-fluid:after,
.container:after,
.dl-horizontal dd:after,
.form-horizontal .form-group:after,
.modal-footer:after,
.nav:after,
.navbar-collapse:after,
.navbar-header:after,
.navbar:after,
.pager:after,
.panel-body:after,
.row:after {
     clear: both;
}
@media (min-width: 768px) {
     .container-fluid {
          width: 1030px;
          background-color: transparent;
          padding: 0 0 0 0;
     }
     .container-fluid {
          padding-right: 15px;
          padding-left: 15px;
          margin-right: auto;
          margin-left: auto;
     }
     .btn-group-vertical>.btn-group:after,
     .btn-group-vertical>.btn-group:before,
     .btn-toolbar:after,
     .btn-toolbar:before,
     .clearfix:after,
     .clearfix:before,
     .container-fluid:after,
     .container-fluid:before,
     .container:after,
     .container:before,
     .dl-horizontal dd:after,
     .dl-horizontal dd:before,
     .form-horizontal .form-group:after,
     .form-horizontal .form-group:before,
     .modal-footer:after,
     .modal-footer:before,
     .nav:after,
     .nav:before,
     .navbar-collapse:after,
     .navbar-collapse:before,
     .navbar-header:after,
     .navbar-header:before,
