@media (min-width: 768px) {
     .navbar-brand {
          display: none;
     }
}
.sdl-res-header {
     height: 40px;
}
.sdl-res-header-color {
     background-color: transparent;
}
/** Drop down menu ***/

.sdl-res-ddmenu-main-ul {
     text-align: left;
     display: inline;
     list-style: none;
}
.sdl-res-ddmenu-main-ul li {
     display: inline-block;
     margin-left: -14px;
     position: relative;
     padding: 5px 5px 5px 5px;
     background: transparent;
     color: #000;
     cursor: pointer;
}
.sdl-res-ddmenu-main-ul li:hover {
     color: #000;
}
.sdl-res-ddmenu-main-ul li:hover .sdl-res-ddmenu-slide {
     display: block;
     opacity: 1;
     visibility: visible;
     z-index: 10000;
}
.sdl-res-ddmenu-slide {
     padding: 10px;
     position: absolute;
     top: 5px;
     width: 150px;
     box-shadow: none;
     display: none;
     opacity: 0;
     background: #C7C7CC;
     /* For browsers that do not support gradients */
     
     background: -webkit-linear-gradient(#F7F7F7, #C7C7CC);
     /* For Safari 5.1 to 6.0 */
     
     background: -o-linear-gradient(#F7F7F7, #C7C7CC);
     /* For Opera 11.1 to 12.0 */
     
     background: -moz-linear-gradient(#F7F7F7, #C7C7CC);
     /* For Firefox 3.6 to 15 */
     
     background: linear-gradient(#F7F7F7, #C7C7CC);
}
.sdl-res-ddmenu-slide li {
     /* Standard syntax */
     
     padding: 5px 5px 5px 10px;
     display: block;
     width: 150px;
     color: #fff;
}
.sdl-res-ddmenu-slide li a {
     color: #000;
     text-decoration: none;
}
.sdl-res-ddmenu-slide li:hover {
     color: #fff;
     background: #aaa;
}
.sdl-res-ddmenu-link {
     color: #000;
     text-decoration: none;
}
.sdl-res-ddmenu-link:hover {
     color: #000;
     text-decoration: none;
}
/***dropdown menu ends ***/
/* Nav links */

.sdl-res-topnav {
     background-color: #EDEDED;
     font-size: 15px;
     /*margin-bottom:20px;*/
     /*background-color: #0F955E;*/
}
.sdl-res-topnav-color {
     background-color: transparent;
     border-radius: 3px 3px 0 0;
     /*border: 1px solid;*/
}
.sdl-res-topnavhome {
     height: 50px;
     font-size: 16px;
     font-weight: 500;
     position: relative;
     display: inline-block;
     padding: 15px;
     max-width: 100px;
     background-color: #438983;
     /* border-radius: 10px 0 0 0;*/
}
.sdl-res-topnavhome a {
     /*color: #cdddeb;    */
     
     color: #fff;
     text-decoration: none;
}
.sdl-res-topnavhome a:hover,
.sdl-res-topnavhome a:focus {
     color: #fff;
     text-decoration: none;
}
.table-bordered {
     border-right: 50px !important;
     border-left: 50px !important;
}
.sdl-res-nav {
     overflow: auto;
}
/** main page styles *********************************************************************************************************************************************/

.sdl-res-table-text {
     font-size: 14px;
     color: #000;
}
.sdl-res-main {
     font-size: 18px;
     line-height: 1.5;
}
@media (min-width: 768px) {
     .container-fluid .navbar-collapse, .container-fluid>.navbar-header, .container>.navbar-collapse, .container>.navbar-header {
          width: 1100px;
          background-color: inherit;
          padding: 0 0 0 250px;
     }
     .navbar-toggle {
          position: relative;
          padding: 10px 10px 10px 10px;
          margin-right: 100px;
          background-color: transparent;
          background-image: none;
          border: 3px solid;
          border-radius: 4px;
     }
     .navbar-right {
          margin-right: 50px;
     }
     .sdl-res-nav-item {
          height: 50px;
          font-size: 16px;
          font-weight: 500;
          color: #000 !important;
          text-decoration: none;
     }
     .sdl-res-nav-item:hover,
     .active,
     .sdl-res-nav-item:focus {
          background-color: transparent !important;
          color: #fff;
          text-decoration: underline;
     }
     .sdl-res-nav-item a,
     .sdl-res-nav-item a:hover {
          color: #fff;
          text-decoration: none;
     }
     .dropdown-menu {
          background: #C7C7CC;
          /* For browsers that do not support gradients */
          
          background: -webkit-linear-gradient(#F7F7F7, #C7C7CC);
          /* For Safari 5.1 to 6.0 */
          
          background: -o-linear-gradient(#F7F7F7, #C7C7CC);
          /* For Opera 11.1 to 12.0 */
          
          background: -moz-linear-gradient(#F7F7F7, #C7C7CC);
          /* For Firefox 3.6 to 15 */
          
          background: linear-gradient(#F7F7F7, #C7C7CC);
          /* Standard syntax */
     }
     a.sdl-res-nav-item {
          padding-top: 15px !important;
          padding-bottom: 15px !important;
     }
}
@media (max-width: 768px) {
     .sdl-res-nav-item:hover, .active, .sdl-res-nav-item:focus {
          background-color: transparent !important;
          text-decoration: none;
     }
}
