/** Announcement related styles start**/

.sdl-res-announcements-roundedbox {
     margin: 10px;
     border: 0px solid #FCEBBB;
     /* border-radius: 10px 10px 10px 10px;*/
}
.sdl-res-announcements-innerbox {
     padding: 10px;
}
.sdl-res-announcements-item {
     padding: 10px;
     border-bottom-color: transparent;
     border-bottom: 0px solid transparent;
}
.sdl-res-announcements-item:last-of-type {
     border-bottom: none;
}
/** Announcement related styles ends **/
/* Pagination */

.pager {
     margin-bottom: 60px;
     text-align: left;
}
.pager > li > a {
     width: 140px;
     padding: 10px 20px;
     text-align: center;
     border-radius: 30px;
}
.sdl-res-module {
     background-color: transparent;
     padding: 10px;
     margin-top: 10px;
}
.sdl-res-module-header {
     background-color: transparent;
     border-bottom: 0px;
     font-size: 18px;
     color: #000000;
     padding-bottom: 10px;
}
.sdl-res-module-sub-header {
     background-color: transparent;
     border-bottom: 0px solid;
     font-size: 15px;
     color: #000000;
     padding-bottom: 10px;
}
.sdl-res-module-body {
     background-color: transparent;
     font-size: 14px;
     color: #000000;
}
.sdl-res-module-item {
     background-color: transparent;
     padding: 10px;
     border: 1px solid transparent;
     border-bottom: 0px solid #000;
     text-decoration: none;
}
.sdl-res-module-item:hover {
     background-color: transparent;
     text-decoration: underline;
     border: 1px solid transparent;
}
.sdl-res-module-item a,
.sdl-res-module-item a:hover {
     text-decoration: none;
