     color: #000;
}
.sdl-res-module-row {
     padding: 5px;
     padding-left: 10px;
}
.sdl-res-module-label {
     font-size: 15px;
     color: #000;
}
.sdl-res-module-section {
     padding: 10px;
}
.sdl-res-module-section-title {
     font-size: 18px;
     color: #000;
}
.sdl-res-module-section-text {
     font-size: 15px;
     color: transparent;
}
.sdl-res-clear-padded-box {
     padding: 10px;
     border: 1px solid;
}
.sdl-res-home-imagebackground {
     background-color: #000000;
     width: 50px;
     height: 50px;
     padding-bottom: 10px;
     display: block;
     margin-left: auto;
     margin-right: auto;
     border-radius: 10px;
}
.sdl-res-home-imagebackground:Hover {
     background-color: transparent;
     width: 50px;
     height: 50px;
     padding-bottom: 10px;
     display: block;
     margin-left: auto;
     margin-right: auto;
     border-radius: 10px;
}
.sdl-home-column {
     padding-bottom: 20px;
     min-height: 120px;
     text-align: center;
     border-right: 0px solid #000;
}
.sdl-home-column:last-of-type {
     border-right: none;
}
a,
a:hover {
     text-decoration: none;
}
.sdl-res-sidetab-button {
     margin-bottom: 10px;
     font-size: 18px;
     color: #000;
     text-align: center;
     background-color: transparent;
     border-radius: 0px 0px 0px 0px;
     padding: 1px 1px 1px 0px;
}
.sdl-res-sidetab-button:hover {
     margin-bottom: 10px;
     color: #fff;
     text-decoration: none;
     font-size: 18px;
     background-color: transparent;
     padding: 0px 0px 0px 0px;
}
.pagination>.active>a,
.pagination>.active>a:focus,
.pagination>.active>a:hover,
.pagination>.active>span,
.pagination>.active>span:focus,
.pagination>.active>span:hover {
     z-index: 2;
     color: #000;
     cursor: default;
     background-color: transparent;
     border-color: transparent;
}
.pagination>li>a,
.pagination>li>span {
     position: relative;
     float: left;
     padding: 6px 12px;
     margin-left: -1px;
     line-height: 1.42857143;
     color: #000;
     text-decoration: none;
     background-color: transparent;
     border: 0px solid #ddd;
}
.pagination>li:first-child>a,
.pagination>li:first-child>span {
     margin-left: 0;
     border-top-left-radius: 4px;
     border-bottom-left-radius: 4px;
}
.pagination>.disabled>a,
.pagination>.disabled>a:focus,
.pagination>.disabled>a:hover,
.pagination>.disabled>span,
.pagination>.disabled>span:focus,
.pagination>.disabled>span:hover {
     color: #000;
     cursor: not-allowed;
     background-color: transparent;
     border-color: transparent;
}
div.all-cases-section {
     margin-bottom: 20px;
}
.pull-left {
     float: right;
}
.container {
     position: relative;
     background-color: transparent;
     padding-right: 5px;
     padding-left: 5px;
     margin-right: auto;
     margin-left: auto;
     border: transparent;
}
.row {
    margin-right: -15px;
    margin-left: -15px;
}

* {
     -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
     box-sizing: border-box;
}
div.dataTables_scrollHead table.table-bordered,
table.table-bordered.dataTable tbody td,
table.table-bordered.dataTable tbody th {
     border-bottom-width: 0;
     color: #000;
}
table.table-bordered.dataTable td,
table.table-bordered.dataTable th {
     border-left-width: 0;
}
.table-bordered>tbody>tr>td,
.table-bordered>tbody>tr>th,
.table-bordered>tfoot>tr>td,
.table-bordered>tfoot>tr>th,
.table-bordered>thead>tr>td,
.table-bordered>thead>tr>th {
     border: 0px solid #ddd;
}
.table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th {
     padding: 8px;
     line-height: 1.42857143;
     vertical-align: top;
     border-top: 0px solid #ddd;
}
.input-group .form-control,
.input-group-addon,
.input-group-btn {
     display: table-cell;
}
.input-group .form-control {
     position: relative;
     z-index: 2;
     float: left;
     width: 100%;
     margin-bottom: 0;
}
.input-group {
     position: relative;
     display: table;
     border-collapse: collapse;
}
.form-control {
     display: block;
     width: 100%;
     height: 34px;
     padding: 7px 7px;
     font-size: 14px;
     line-height: 1.42857143;
     color: #555;
     background-color: transparent;
     background-image: none;
     border: 0px solid #ccc;
     border-radius: 4px;
     -webkit-box-shadow: inset 2 3px 3px rgba(0, 0, 0, .075);
     box-shadow: inset 2px 3px 3px rgba(0, 0, 0, .075);
     -webkit-transition: border-color ease-in-out .15s, -webkit-box-shadow ease-in-out .15s;
     -o-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
     transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
}
.input-group-btn:last-child>.btn,
.input-group-btn:last-child>.btn-group {
     z-index: 2;
     margin-left: -1px;
}
.input-group .form-control:last-child,
.input-group-addon:last-child,
.input-group-btn:first-child>.btn-group:not(:first-child)>.btn,
.input-group-btn:first-child>.btn:not(:first-child),
.input-group-btn:last-child>.btn,
.input-group-btn:last-child>.btn-group>.btn,
.input-group-btn:last-child>.dropdown-toggle {
     border-top-left-radius: 0;
     border-bottom-left-radius: 0;
}
.input-group-btn>.btn {
     position: static;
}
.btn-default {
     color: #333;
     background-color: transparent;
     border-color: #ccc;
}
.btn {
     display: inline-block;
     padding: 6px 12px;
     margin-bottom: 0;
     font-size: 14px;
     font-weight: 400;
     line-height: 1.42857143;
     text-align: center;
     white-space: nowrap;
     vertical-align: middle;
     -ms-touch-action: auto;
     touch-action: auto;
     cursor: pointer;
     -webkit-user-select: none;
     -moz-user-select: none;
     -ms-user-select: none;
     user-select: none;
     background-image: none;
     border: 0px solid transparent;
     border-radius: 4px;
}
/** Footer styles *********************************************************************************************************************************************/

