.sdl-res-footer-item-large:hover,
.sdl-res-footer-item-large:focus {
     color: #fff;
     text-decoration: none;
}
.sdl-res-footer-image-align {
     margin-top: 3px;
}
.sdl-res-footer-dark {
     color: transparent;
     text-align: center;
     background-color: transparent;
}
.sdl-res-footer-item-small {
     position: relative;
     display: inline-block;
     padding: 10px;
     font-size: 10px;
     color: #000;
     background-color: transparent;
     text-decoration: none;
}
.sdl-res-footer-item-small:hover,
.sdl-res-footer-item-small:focus {
     background-color: transparent;
     color: #fff;
     text-decoration: none;
}
.sdl-res-table-text-align-left {
     text-align: left;
}
.sdl-res-table-text-align-center {
     text-align: left;
}
.sdl-res-table-text {
     font-size: 12px;
     color: transparent;
}
.sdl-res-table-text-bold {
     font-weight: bold;
}
.sdl-res-border {
     border-right: 0px solid #D1D5D8;
     border-left: 0px solid #D1D5D8;
     border-bottom: 0px solid #D1D5D8;
}
.loading-overlay {
     min-width: 100%;
     position: absolute;
     left: 0;
     top: 0;
     bottom: 0;
     right: 0;
     z-index: 9999;
     cursor: not-allowed;
}
.loading-image {
     padding-bottom: 0px;
}

div.article-field-section {
    overflow: auto;
    overflow-wrap: break-word;
}

div.article-field-section .container {
     width: 100% !important;
     
}
div.article-field-section table {
     width: 100%;
     table-layout: fixed;
}

