     word-wrap: break-word;
     /* IE 5.5+ */
}
.search-error {
     color: #a94442;
     display: inline;
     font-weight: bolder;
     font-size: 14px;
}
div:not(.has-error) + span.help-block {
     display: none;
}
div.has-error + span.help-block {
     display: inline;
}
select.form-control {
     height: 32px !important;
}
