css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.meppelercourant.nl"), domain("www.emmen.nu") {

  .comp-nieuws-detail-text {
    font-size: 1.2em;
    line-height: 170%;
    width: 90%;
  }
  .comp-nieuws-detail-text h2 {
    margin-bottom: 5px;
  }
  
  p {
    margin: 0 0 25px 0;
  }
  
  .comp-nieuws-detail-meta,
  .comp-nieuws-detail-delen,
  #zoeken-widget,
  .dossiers-widget-icon,
  .dossiers-widget-item-title,
  .blogs-widget-icon {
    display: none;
  }
  
  #side-sidebar-list {
    margin-top: 38px;
  }
  
  .comp-nieuws-detail-titlebar {
    font-size: 2.3em;
    padding-bottom: 0;
    position: relative;
    margin-right: -315px;
  }
  .comp-nieuws-detail-title {
    margin-top: 10px;
  }
  
  .comp-nieuws-detail-container {
    padding-top: 0;
    margin-top: 0;
  }
  .comp-nieuws-detail-sliderbox {
    width: 105%;
    margin-left: -40px;
  }
  .comp-nieuws-detail-flex-caption {
    margin-top: 5px;
    margin-left: 40px;
  }
  
  #site-sidebar-list {
    margin-top: 37px !important;
  }
  
  #panel-footer-colofon-container {
    margin-bottom: 0px;
  }
  
  .dossiers-widget,
  .blogs-widget {
    padding: 25px 0;
    border: 0px solid black !important;
    background-color: transparent;
  }
  .dossiers-widget-header,
  .blogs-widget-header {
    background-color: transparent;
    border: 0px solid black;
    padding: 0;
    margin: 0;
    height: auto;
    text-indent: 5px;
  }
  .dossiers-widget-header a, 
  .dossiers-widget-header span,
  .blogs-widget-header a,
  .blogs-widget-header span {
    font-family: 'DroidSerifRegular';
    line-height: inherit;
    font-size: 1.8em;
    text-transform: lowercase;
    display: inline-block;
  }
  .dossiers-widget-header a:first-letter, 
  .dossiers-widget-header span:first-letter,
  .blogs-widget-header a:first-letter,
  .blogs-widget-header span:first-letter {
    text-transform: capitalize;
  }
  .dossiers-widget-item,
  .blogs-widget-item {
    border-bottom: 0px;
  }
  .dossiers-widget-image-box,
  .blogs-widget-image-box {
    position: relative;
    float: right;
    width: auto;
    top: 0;
  }
  .dossiers-widget-item-text-block,
  .blogs-widget-item-text-block {
    margin-left: 0 !important;
  }
  .dossiers-widget-item-text,
  .blogs-widget-item-text {
    font-size: 1.1em;
  }
  .dossiers-widget-item-text:hover {
    text-decoration: underline;
  }
  .blogs-widget-item-text-block {
    padding-left: 10px;
    width: auto;
    margin-right: 83px;
  }
  
  .blogs-widget-item {
    padding-top: 10px;
    padding-bottom: 10px;
  }
  .blogs-widget-header,
  .blogs-widget-item {
    padding-left: 10px;
    border-left: 1px solid rgba(0,0,0,0.05);
    margin-left: -10px;
  }
  .blogs-widget-item-title a {
    font-size: 1.3em;
    font-weight: normal;
    font-family: 'DroidSerifRegular';
    
  }
  
  
  
}
