css:
@-moz-document domain("www.lesnumeriques.com") {
body {
    background-color: #222 !important;
    color: #999 !important;
}
a {
    color: #fff !important;
}
#content, .content-v5, .home-v5, .good-plans, .main-header-cat-trend ul.main-header-types, .main-header-types-container, .main-header-types, .block-more, .block-header-title, .block-more a, .main-header, .main-header-container, #carrousel a span, #home-v5 .box-compare .block-header, .home-most ul, .home-most-commented-list li .home-most-img-corner, .home-most-img-corner, .home-most-read-list li .home-most-img-corner, .box-category-name, #main.product-v4 #article-related, #main.product-v4 #article-related a, .box-compare-categories, .box-compare-counts-number {
    background-color: #333 !important;
    color: #999 !important;
    text-align: justify;
}
#content, .content-v5 {
    border: 1px solid #999 !important;
    margin-top: 10px !important;
}
#home-v5 .box-compare, .homepage-block {
    border-top: 1px solid #999 !important;
    background-color: #333 !important;
    color: #999 !important;
    padding: 20px 16px 5px !important;
    margin-left: -10px !important;
    margin-right: -10px !important;
}
.home-arrow-universe {
    background-color: #333 !important;
}
.publication-breadcrumbs, .card .card-transparent {
    background-color: #222 !important;
}
h1, h2, h3, h4, p, .text, .main-header a, #main.publication-v4>div.content>.text .mini-product-price .users-note a, .publication-inset {
    color: #999 !important;
}
.main-header a:hover {
    color: #fff !important;
}
#main.publication-v4>div.content .ln__price .ln__price__offers>ul>li>a .ln__price__offers__price, .ln__price .ln__price__offers>ul>li>a .ln__price__offers__price, #main.publication-v4>div.content .ln__price__md .ln__price__offers>ul>li.ln__price__offers__item--is-highlight>a .ln__price__offers__price::before, .ln__price__md .ln__price__offers>ul>li.ln__price__offers__item--is-highlight>a .ln__price__offers__price::before {
    background-color: #222 !important;
    color: #999 !important;
    border: 0 !important;
}
.content .highlight-v4 span.text:before, .search-suggest:before {
    background-color: #222 !important;
}
#main.product-v4 #product-ads>div, #main.product-v4 #product-body>#product-content, #main.product-v4 #product-comments>div {
    width: initial !important;
}
.home-most-title.mod-active, .home-most-commented-title {
    border: 1px solid #999 !important;
    color: #999 !important;
    background-color: #333 !important;
}
#main.publication-v4 h1.title {
    border-top: 1px solid #999 !important;
}
#main {
    width: 100% !important;
}
#aside, footer, .block-compare, .most-aside, .tests-aside {
    display: none !important;
}
.block-news .block-stack, .block-selections .plain-card-left, .block-tests .block-stack, .block-videos .block-stack {
    margin: 0 !important;
}
#articles-related-authors #authors-infos, #articles-related-authors #authors-infos span.description, #topic-comments, #topic-comments .even, #main.publication-v4>div.content .ln__price .ln__price__tabs>li a, .ln__price .ln__price__tabs>li a, #main.publication-v4>div.content .ln__price .ln__price__tabs>li.ln__price__tab__active a, .ln__price .ln__price__tabs>li.ln__price__tab__active a {
    background-color: #222!important;
    color: #999 !important;
}
.uneditable-input, input[type=text], input[type=password], input[type=datetime], input[type=datetime-local], input[type=date], input[type=month], input[type=time], input[type=week], input[type=number], input[type=email], input[type=url], input[type=search], input[type=tel], input[type=color], textarea, .main-header-search input[type=text] {
    color: #999 !important;
    background-color: #555 !important;
    border: 1px solid #999!important;
}
#site-header-v4 {
    position: absolute !important;
    background-color: #222 !important;
    color: #fff !important;
}
#site-header-v4 > div ul#categories > li > a {
    color: #999 !important;
}
#site-header-v4 > div form#search-form-v4 input[type="text"] {
    color: #999 !important;
    background: url(http://www.lesnumeriques.com/css/images_v2/header-search-v2.png) center center no-repeat #333 !important;
}
.main-header-logo.mod-logo-fr {
    color: #fff !important;
}
#main > div, #main.product-v4, #main.product-v4 > div, #main.product-v4 #product-body #specs, #main.product-v4 #product-body .chapitre-content.boxed-content, #aside.aside-v4 div#box-partners-v4, #aside.aside-v4 div#product-reviews-list, #aside.aside-v4 #product-reviews-list > div #reviews-list-dropdown, #aside.aside-v4 > div#product-last-added, #box-buyers-guides, #box-buyers-guides > div, .hover-cat.cat-10, .hover-cat.cat-11, .hover-cat.cat-12, .hover-cat.cat-13, .hover-cat.cat-14, .hover-cat.cat-15, .hover-cat.cat-16, .hover-cat.cat-17, #aside #box-selected-articles-v4, #aside.aside-v4 > div, #timeline > div, #timeline ul .publication, #timeline ul .publication-right, #timeline ul.grid .publication .text h3, #timeline ul.grid .publication-right .text h3  {
    background-color: #222 !important;
    color: #999 !important;
}
#aside.aside-v4 #product-reviews-list::before {
    background-image:none !important;
}
#main.product-v4 #product-body #product-navigation {
    display: none !important;
}
div#box-youtube.box-youtube, div.product-v4 #product-ads, footer.footer-v4 {
    display: none !important;
}
}
