css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(iunctis.fr) {

/* BACKGROUND */

body {
    background-color: #09090F;
}

html {
    background-color: #09090F;
}

   

/* HEADER */
 
.d-header {
    background-color: #11111D;
    box-shadow: 0 3px 3px 0px rgba(0,0,0,0.1);
}

.d-header .icons .unread-notifications {
    background-color: #5294e2;
    color: black;
}

.unread-private-messages.badge-notification[href] {
    color: black;
    background-color: #E9753F;
}



/* POP-UP */

.modal-inner-container {
    background-color: #131220;
    border: 1px solid #1d1c31;
    box-shadow: 0 3px 7px rgba(0,0,0,0.8);
}



/* ABANDON MESSAGE */
 
.bootbox.modal {
    background-color: #131220;
    border: 1px solid #1d1c31;
    box-shadow: 0 3px 7px rgba(0,0,0,0.8);
}

body .bootbox.modal .modal-footer a.btn-primary {
    color: #d9d9d9;
    background-color: #cc575d;
    transition: background-color 0.5 ease-in-out;
}

.modal-footer {
    border-top: 1px solid #1d1c31;
}

body .bootbox.modal .modal-footer a.btn-primary:hover {
    background-color: #bf3c43;
}

a.btn.null {
    color: #d9d9d9;
    background-color: #5294e2;
}



/* MESSAGES */
 
.topic-body {
    background-color: #11111D;
    border-top: 1px solid #1c1c30 !important;
}

.topic-avatar {
    border-top: 1px solid #1c1c30 !important;
}

.topic-area {
    background-color: #11111D;
}

#topic-closing-info {
    border-top: 1px solid #1c1c30;
}

.deleted .topic-body {
    background-color: #67282f;
}



/* OUTILS ADMIN-MODERATION */

.show-topic-admin {
    background: #cc575d;
    color: #d9d9d9;
    transition: color 0.5s ease-in-out;
    transition: background-color 0.5s ease-in-out;
}

.show-topic-admin:hover {
    background: #c14c52;
    color: #d9d9d9;
}

.popup-menu {
    background-color: #151525;
    border: 1px solid #252541;
}

.popup-menu .btn {
    background-color: #252541;
    transition: background-color 0.5s ease-out;
}

.popup-menu .btn:hover {
    color: #d9d9d9;
    background-color: #383861;
}

.btn-danger {
    color: #d9d9d9;
    background: #cc575d !important;
    transition: background-color 0.5s ease-out;
}

.btn-danger:hover {
    color: #d9d9d9;
    background-color: #c14c52 !important;;
}



/* PRECODE RAPIDE */

p > code, li > code, pre > code {
    color: #f8f8f8;
    background: #252541;
    padding: 2.5px;
}



/* ANNONCE LE MESSAGE VISIONNE */
 
.topic-body.highlighted, #topic-list .topic-list-item.highlighted {
    background-color: #1c1c30;
}



/* GLYPHE MP */
  
.private-message-glyph {
    color: #cc575d;
}



/* APPERCU DES REPONSES */
  
div.reply {
    background-color: #1c1c30;
}

.embedded-posts .fa-arrow-up, .embedded-posts .fa-arrow-down {
    color: #d9d9d9;
}

.embedded-posts .topic-body {
    background: #1c1c30 !important;
}

.embedded-posts .quote .title {
    background: #262640;
    border-left: 5px solid #cc575d;
}

.embedded-posts blockquote{
    background: #262640;
    border-left: 5px solid #cc575d;
}



/* BANDEAU MESSAGE SUPPRIME */

.gap.jagged-border {
    background-color: #67282f;
    background-image: none;
    padding: 10px 3px;
    margin-left: -5px;
}



/* AVERTISSEMENT SPAM GROUPE SUR PREVIEW */

.composer-popup {
    color: #d9d9d9;
    background: #cc575d;
}

.composer-popup.education-message {
    background-color: #5294E2;
    color: #d9d9d9;
}



/* COULEURS MODERATION */

.moderator article {
    background: #181827;
    margin-right: 283px;
    margin-left: -5px;
    padding-left: 5px;
}
  
.moderator .topic-body {
    background-color: #181827;
}

aside.onebox .onebox-body {
    width: 700px;
    background-color: #1D1D30;
}

 
/* RESUME DE SUJET */

.badge-notification.clicks {
    background-color: #319b4a;
    color: #d9d9d9;
}

.topic-map {
    margin: 20px 0 0 0;
    background: #1c1c30;
    border: 1px solid #262640;
}

.topic-map section {
    border-top: none;
}

.topic-map .avatars, .topic-map .links, .topic-map .information {
    padding: 7px 10px 15px 10px;
    color: #d9d9d9;
    border: 1px solid #262640;
}

.topic-map .buttons .btn {
    border: 0;
    border-bottom: 2px solid #cc575d;
    padding: 0 23px;
    color: #d9d9d9;
    background: #cc575d;
    transition: background 0.5s ease-out;

}

.topic-map .buttons .btn:hover {
    background: #c14c52;
    color: #d9d9d9;
    border-bottom: 2px solid #c14c52 !important;
}



/* SONDAGE */
 
div.poll {
    background-color: #1c1c30;
    border: 1px solid #262640;
}

div.poll .poll-info {
    border-left: 1px solid #262640;
    color: #d9d9d9;
}

div.poll .poll-buttons {
    border-top: 1px solid #262640;
}

div.poll .poll-buttons button {
    background: #252541 !important;
    color: #d9d9d9;
    transition: background-color 0.5s ease-out;
}

div.poll .poll-buttons button:hover {
    background: #383861 !important;
    color: #d9d9d9;
}

div.poll .results .bar {
    background: #CC575D;
}

.ember-view.toggle-status.btn-danger.btn {
    color: #d9d9d9;
    background: #cc575d !important;
    transition: background-color 0.5s ease-out;
}

.ember-view.toggle-status.btn-danger.btn:hover {
    color: #d9d9d9;
    background-color: #c14c52 !important;;
}

div.poll li[data-poll-option-id] {
    color: #d9d9d9;
    background: #252541;
    box-shadow: none;
    padding: .5em .7em .5em .5em;
    transition: background 0.5s ease-out;
}

div.poll li[data-poll-option-id]:hover {
    background: #383861;
    color: #d9d9d9;
    box-shadow: none;
}

div.poll li[data-poll-option-id][data-poll-selected="selected"] {
    background: #cc575d !important;
}



/* MENTIONS */
  
a.mention {
    padding: 3px 3px;
    color: #d9d9d9;
    background: #383861;
    border-radius: 6px;
}

a.mention-group {
    padding: 3px 3px;
    color: #d9d9d9;
    background: #cc575d;
    border-radius: 6px;
}

a.hashtag {
    padding: 3px 3px;
    color: #d9d9d9;
    background: #383861 none repeat scroll 0% 0%;
    border-radius: 8px;
}

a.hashtag:visited, a.hashtag:hover {
    color: #e0b271;
}



/* BOUTONS SUR MESSAGE */
  
nav.post-controls .post-admin-menu {
    background-color: #151525;
    border: 1px solid #252541;
    box-shadow: 0 3px 7px rgba(0,0,0,0.8);
}

nav.post-controls .show-replies {
    color: #d9d9d9;
    background: #252541 !important;
    border: none;
    font-weight: normal;
    border-bottom: 2px solid #5294e2 !important;
    transition: background-color 0.5s ease-in-out;
}

nav.post-controls .show-replies:hover {
    background-color: #383861 !important;
}



/* LISTE SUJETS */

.topic-list > tbody > tr {
    border-bottom: none;
}

.topic-list {
    background-color: #0e0e18;
    color: #d9d9d9;
    border-top: 1px solid #e9753f;
}

.topic-list a.title.visited:not(.badge-notification) {
    color: #d9d9d9;
}

.topic-list > tbody > tr:first-of-type {
    border-top: 1px solid #272541;
    background-color: #0d0d14;
}

.topic-list > tbody > tr:nth-child(2n) {
    background-color: #131220;
}

.topic-list > tbody > tr:nth-child(2n+1) {
    background-color: #0d0d14;
}

.badge-notification.new-posts, .badge-notification.unread-posts {
    color: black;
    background-color: #5294e2;
}

.topic-list .fa-thumb-tack {
    color: #E9753F;
}

.topic-list .fa-bookmark {
    color: #5294E2;
}

.topic-list td {
    color: #7887a1;
}



/* SORTABLE */

.topic-list .sortable {
    background: #0e0e18;
    transition: background-color 0.5s ease-in-out;
}

.topic-list .sortable:hover {
    background-color: #11111d;
    transition: background-color 0.5s ease-in-out;
}



/* ALERTE NOUVEAU MESSAGE */

.alert.alert-info {
    background: #434370;
    transition: background-color 0.5s ease-in-out;
}

.alert.alert-info:hover {
    background-color: #56568f;
}



/* BOUTONS */

#topic-footer-buttons .btn {
    color: #d9d9d9;
    background: #252541 !important;
    font-weight: normal;
    border-bottom: 2px solid #5294e2 !important;
    transition: background-color 0.5s ease-in-out;
}

#topic-footer-buttons .btn:hover {
    background-color: #383861 !important;
}

.nav-pills > li.active > a, .nav-pills > li > a.active {
    color: #d9d9d9;
    background-color: #E9753F;
}

.nav-pills > li > a {
    background-color: transparent;
    transition: color 0.5s ease-in-out;
}

.nav-pills > li > a:hover {
    color: #E9753F;
    background-color: transparent;
}



/* CREATE TOPIC */

button#create-topic.btn {
    background: #252541;
    color: #d9d9d9;
    border-bottom: 2px solid #5294e2;
    transition: background-color 0.5s ease-in-out;
}

button#create-topic.btn:hover {
    background-color: #383861;
}



/* EDIT CAT AND DROP ROND */

button.btn-default.edit-category.btn {
    background: #252541;
    color: #d9d9d9;
    border-bottom: 2px solid #5294e2;
    transition: background-color 0.5s ease-in-out;
}

button.btn-default.edit-category.btn:hover {
    background-color: #383861;
}

button.btn.standard.dropdown-toggle {
    background: #252541;
    color: #d9d9d9;
    border-bottom: 2px solid #5294e2;
    transition: background-color 0.5s ease-in-out;
}

button.btn.standard.dropdown-toggle:hover {
    background-color: #383861;
}

#user-card .card-content a.btn {
    color: #d9d9d9;
    background: #252541 !important;
    border: none;
    font-weight: normal;
    border-bottom: 2px solid #5294e2 !important;
    transition: background-color 0.5s ease-in-out;
}

#user-card .card-content a.btn:hover {
    background-color: #383861 !important;
}

#user-card .card-content {
    background: rgba(17,17,29,0.85);
}

.quote-button {
    background: #252541 !important;
    color: #d9d9d9;
    transition: background-color 0.5s ease-out;
}

.quote-button:hover {
    background: #383861 !important;
    color: #d9d9d9;
}



/* ONEBOX  */

blockquote {
    border-left: 5px solid #cc575d !important;
    background-color: #1d1d30;
    color: #d9d9d9;
}

aside.quote .title {
    border-left: 5px solid #cc575d !important;
    background-color: #1d1d30;
    color: #d9d9d9;
}

aside.onebox.whitelistedgeneric {
    background-color: #1d1d30;
    border-left: 5px solid #43c461 !important;
    }

pre code {
    background-color: #1d1d30;
    border-left: 5px solid #29303b !important;
}

aside.onebox.twitterstatus {
    background-color: #1d1d30;
    border-left: 5px solid #25aae2 !important;
}

aside.onebox.wikipedia {
    background-color: #1d1d30;
    border-left: 5px solid #2b882b !important;
}



/* DROPDOWN */
 
.list-controls .category-breadcrumb li.bullet > .badge-category {
    background: #131328 !important;
    color: #d9d9d9;
}

.list-controls .category-dropdown-menu {
    max-width: 184px;
    border: 1px solid #252541;
    background: #151525;
    transition: background-color 0.5s ease-out;
    box-shadow: 0 3px 3px 0px rgba(0,0,0,0.2);
}

.list-controls .category-dropdown-menu .cat{
    background: #151525;
    transition: background-color 0.5s ease-out;
}

.list-controls .category-dropdown-menu .cat:hover {
    background-color: #252541;
}


 
/* HAMBURGER */
 
.menu-panel {
    border: 1px solid #252541;
    background-color: #151525;
}

.user-menu .notifications .read {
    background-color: #151525;
    transition: background-color 0.5s ease-out;
}

.user-menu .notifications .read:hover {
    background-color: #252541;
}

.user-menu .notifications li p {
    color: #5294e2;
}

.user-menu .notifications li a {
    color: #5294e2;
}

.menu-panel ul li.heading a {
    background-color: #151525;
    transition: background-color 0.5s ease-out;
}

.menu-panel ul li.heading a:hover {
    background-color: #252541;
}

.menu-panel ul.menu-links li a, .menu-panel ul li.heading a {
    color: #5294e2;
    transition: background-color 0.5s ease-out;
}

.menu-panel ul.menu-links li a:hover, .menu-panel ul li.heading a:hover {
    background-color: #565c6b;
}

.user-menu .notifications li {
    background-color: #383861;
    transition: background-color 0.5s ease-out;
}

.user-menu .notifications li:hover {
    background-color: #4a4a82;
}


/* PARTIE AVATAR */

.drop-down-visible .d-header .icons .active .icon {
    background-color: #151525;
    border-top: 1px solid #252541;
    border-left: 1px solid #252541;
    border-right: 1px solid #252541;
}



/* WIDGET */

#topic-progress {
    background-color: #252541;
    color: #d9d9d9;
    border: 1px solid #383861;
    border-bottom: none;
    box-shadow: 0 0px 2px rgba(0,0,0,0.3);
}

#topic-progress .bg {
    border-right: 1px solid #383861;
    background-color: #4a4a82;
}

#topic-progress-expanded .btn {
    border: none;
    color: #d9d9d9;
    background: #252541;
    transition: background-color 0.5s ease-out;
}

#topic-progress-expanded .btn:hover {
    background-color: #383861;
}

#topic-progress i.fa {
    color: #d9d9d9;
}

#topic-progress-expanded {
    border: 1px solid #252541;
    background: #151525;
    width: 135px;
    box-shadow: 0 0px 2px rgba(0,0,0,0.2);
}

#topic-progress-expanded .jump-form input[type="text"] {
    background-color: #11111d;
    border: 1px solid #252541;
    border-radius: 3px;
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.3);
}



/* PROFIL PRESO */

.user-main .user-stream .item {
    padding: 20px 8px 15px 8px;
    background-color: #11111d;
    border-bottom: 1px solid #1c1c30;
}

.user-main .about .details {
    background: rgba(17,17,25,0.85);
}

.user-main .user-content {
    padding: 10px 8px;
    background-color: #11111d;
    border: 1px solid #252541;
    margin-bottom: 10px;
    box-sizing: border-box;
}

.badge-card .badge-contents .badge-icon {
    background-color: #1A1A2C;
}

.badge-card .badge-contents {
    background-color: #151525;
}

.replies-section li {
    border-left: 5px solid #cc575d !important;
    background-color: #1d1d30;
}

.stats-section li {
    background: #1D1D30;
}

.topics-section li {
    border-left: 5px solid #25aae2 !important;
    background-color: #1d1d30;
}

.links-section li {
    border-left: 5px solid #43c461 !important;
    background-color: #1d1d30;
}

.likes-section li {
    border-left: 5px solid #fa6c8d !important;
    background-color: #1d1d30;
}



/* REPLY CONTROL */

#reply-control.open .grippie {
    background-color: #1c1c30;
}

body .grippie {
    width: 100%;
    border-top: 1px solid #262640 !important;
    background-color: #262640;
    box-shadow: 0px -1px 3px 0px rgba(0,0,0,0.1);
}

#reply-control {
    background-color: #131320 !important;
}

#reply-control .d-editor-button-bar {
    color: #d9d9d9;
    background-color: #11111d;
    border-color: #262640;
}

#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview {
    color: #d9d9d9;
    background-color: #11111d;
    border-color: #262640;
}

#reply-control .wmd-controls .d-editor, #reply-control .wmd-controls .d-editor-container, #reply-control .wmd-controls .d-editor-textarea-wrapper, #reply-control .wmd-controls .d-editor-preview-wrapper {
    border-color: #262640;
}

#reply-control .d-editor-button-bar button {
    transition: color 0.5s ease-in-out;
}

#reply-control .d-editor-button-bar button:hover {
    color: #cc575d;
    background-color: transparent;
}



/* MP */

#reply-control.edit-title .contents input#reply-title {
    background-color: #09090F;
    border-color : #252541;
    color: #d9d9d9;
}

#private-message-users {
    background: #09090F;
}

div.ac-wrap {
    background-color: #11111D;
    border: 1px solid #252541;
    padding: 5px 4px 1px 4px;
}



/* POP UP EMOJI SELECTION RAPIDE */

.autocomplete {
    width: 240px;
    background-color: #151525;
    color: #d9d9d9;
    border: 1px solid #252541;
}

.autocomplete ul li {
    border-bottom: 1px solid #252541;
}

.autocomplete ul li a.selected[href] {
    background-color: #262640;
}

.discourse-no-touch .autocomplete ul li a[href] {
    color: #d9d9d9;
    transition: background-color 0.5s ease-out;
}

.discourse-no-touch .autocomplete ul li a[href]:hover {
    background-color: #252541;
    text-decoration: none;
}



/* POP UP MENTION COULEUR PSEUDO */

.autocomplete ul li a[href] span.username {
    color: #5294e2;
}

}
