css:
@-moz-document domain("shizafm.com") {
body{
	background: url(https://pp.vk.me/c628618/v628618404/485be/sKZGDHjDiaQ.jpg) no-repeat;
    background-size: cover;
    background-color: #141640;
    background-attachment: fixed;
}

.content-nav a{
        border: none;
 		color: rgb(20, 100, 250);
    	border: 1px solid white;
    	background-color: rgba(250, 250, 250, 0.2);
}
.plays a{
        border: none;
}
.block ul{
    border: none;
}
.block{
    border: none;
    color: rgb(71, 169, 236);
    background-color: rgba(0, 0, 0, 0);
}
.player-controls{
    border-right: none !important;
}

.header-logo, .footer-container{
	display: none;
}
.age-warning{
	display: none;
}
.alert{
	opacity: 0.1;
}
.shedule-list .day {
	background: none;
}
.shiza-chat-container button{
    cursor: pointer;
    color: white;
    width: 30%;
    background-color: rgba(71, 169, 236, 0.7);
    height: 20%;
    float: left;
    margin: 5px;
    font-size: 70%;
    line-height: 100%;
}
.shiza-chat-container button:hover {
background-color: rgba(0, 0, 0, 0.7);
}
.label{
	background-color: rgba(50, 120, 250, 1) !important;
}
.strannik, .infoblock, .applets, #themes_menu{
	display: none;
}

tr {
   background-color: rgba(0, 0, 0, 0.2);
   color: white;
}
.search{
    background-color: rgba(0, 0, 0, 0.4);
        color: white;
    padding: 5px;
}
.search_button{
	 background-color: rgba(0, 0, 0, 0.7);
    cursor: pointer;
}
.search_button:hover {
	 background-color: rgba(0, 0, 0, 0.9);
    cursor: pointer;
}
#shedule{
        cursor: pointer;
    background-color: rgba(0, 0, 0, 0.6);
}
#shedule a{
        cursor: pointer;
}

.shiza-chat-text p span.hashtag {
    background-color: rgba(50, 120, 250, 1) !important;
    border-radius: 3px;
    padding: 5px 7px !important;
}
.player-controls .icon, .track-list .plays {
    opacity: 1 !important;
}
.player .bitrates .bitrate {
    opacity: 1;
}
.questions {
    background-color: rgba(0, 0, 0, 0.2);
}
.question {
    background-color: rgba(0, 0, 0, 0);
}
.shiza-chat-text .icon.remove {
    color: white;
    font-size: 22px;
}

#stickers_menu{
	    background-color: rgba(0, 0, 0, 0.3);
}
#stickers_menu li {
    background-color: rgba(0, 0, 0, 0.3);
    box-shadow: none;
}
.shiza-chat-area{
	position: fixed;
    width: 50%;
    left: 5%;
    top: 13%;
    background-color: rgba(206,211,213,.5);
    z-index: 2 !important;
    height: 85%;
    overflow: hidden;
}
.chat-form{
	width: 35%;
    position: fixed;
    left: 60%;
    top: 22%;
    background-color: rgba(206,211,213,.5);
    height: 30%;
}
.content-nav{
	position: fixed;
    width: 35%;
    height: 45%;
    left: 60%;
    top: 52%;
    overflow-y: scroll;
}
.sidebar{
	position: fixed;
    left: 5%;
    top: 0;
    width: 90%;
    height: 10%;
    background-color: rgba(206,211,213,.5);
    border-radius: 0 0 15px 15px;
    border: 1px solid white;
    z-index: 5;
}
.widget{
	width: 50%;
    height: 100%;
    float: left;
}
#shiza-stream img {
    float: left;
    width: 50px;
    height: 70%;
    margin-right: 25px;
}
.shiza-chat-container textarea {
    background-color: rgba(206,211,213,.5);
    position: fixed;
    left: 60%;
    color: rgb(71, 169, 236);
    width: 35%;
    top: 13%;
    padding: 4px;
    margin: 0;
}
.shza-shat-container textarea:focus {
	background-color: rgba(206,211,213,.8);
}
.content-nav{
	background-color: rgba(206,211,213,.5);
    border: 1px solid white;
}
textarea:focus {
    background-color: rgba(206,211,213,.9);
}
a img{
	width: 95%;
    height: auto;
    opacity: .5;
}
a img:hover{
	opacity: 1;
}
.content{
	width: 60%;
}
#stickers{
	z-index: 4 !important;
    position: fixed;
}
}
