css:
@-moz-document domain("www.t411.ch") {
/* COPYRIGHT TOMAKLODXYZ */
/****** FONTS ET AUTRES *******
****************************************
***************************************
@import url(https://fonts.googleapis.com/css?family=Source+Sans+Pro:400,200,200italic,300,300italic,400italic,600,600italic,700,700italic,900,900italic);/
*/
body {
font-family: 'Helvetica', sans-serif;
     text-rendering: optimizeLegibility;
    background: rgba(243, 243, 243, 0.24);
    
}

input[type="search"] {
	-webkit-appearance: none;
}
/*************** HEADER ****************
****************************************
***************TOMAKLOD************************/
.headerPlace {
    position:fixed;
}

.headWrapper { /* MODIF HAUTEUR BACKGROUND RETIRE SHADOW */ 
    background : #013050;
    height: 55px;
    box-shadow:none;
    top: -3px;
                                       
}

.head { /* ON VIRE LES MARGES SUR LES COTÉS */
    width:100%;
}

.logo { /* LOGO PLUS CLEAN */
    background: url("http://lab.tomaklod.xyz/t411/logo_t411.png") no-repeat;
    width: 100%;
    top: -8px;                                    
}

.toolbar { /* ON DEPLACE LA TOOLBAR A COTE DU LOGO */
    position: absolute;
    top: 4px;
    left: 100px;
}

.searchBar /* ON RETIRE LE BACKGROUND DE LA SEARCH BAR */ {
	background: none;
}

.searchInput /* ON CHANGE LE RADIUS DE LA BARRE DE RECHERCHE ET SA HAUTEUR*/ {
	border-radius: 4px;
    width: 515px !important;
    height: 40px;         
    left: -23px;
    top: -3px;
    color: #585858;
    font-size: 15px;
    font-weight:500;
    padding-left: 13px;
}

.searchInput:focus {
    outline-style: none;
}
.searchBtn { /* ICON RECHERCHE */
    background: url("http://lab.tomaklod.xyz/t411/searchbtn.png") no-repeat;
 	left: 500px;
    top: 9px;
    position:absolute;
    float:left;
}
/*************** ICONS ****************
****************************************
***************************************/
.loginBar .mail {
  	background: url("http://lab.tomaklod.xyz/t411/icon-email.png") no-repeat;
}
#bar-search .searchHistory {/* DELETE ICON RECHERCHE PERSONNALISE */ 
   display : none !important;
}

.loginBar .reload {
    display: none;
}

.loginBar {
    background: none;
    position:absolute;
	top: 9px;
    right:0px;
    padding-right:16px;
}

/*************** HEADER ****************
****************************************
COPYRIGHT TOMAKLOD
***************************************/
.loginBarSide {
    display: none !important;
}

.loginBar span {
    color: #fff;
    font-size: 14px;
    -webkit-font-smoothing: subpixel-antialiased !important;
    margin-top: 6px;
}

.up {
    color: #4bd22c !important;
    font-size: 14px !important;
}

.down {
    color: #ef1617 !important;
    font-size:14px !important;
}

.loginBar .sep {
    color: #2f536c;
    margin: 6px 5px 0 5px;
}

.loginBar .slideToo {
    color:#fff;
    margin-top: 6px;
    padding-right:22px;
}
.logout {
    color: #ef1617 !important;
    font-weight:400;
}

/*************** MENU ******************
****************************************
***************************************/

#lavamenu {
	margin-top: 54px !important;
    margin:0;
    float:left;
    width:100%;
    background:#002640 !important;
    height:50px;   
    padding-left:20px;
}

#lavamenu>li>a { /* MARGES */
	padding: 13px 10px;
}

#lavamenu li a {
	font-size: 15px;
}
    

/* COPYRIGHT TOMAKLODXYZ */
#lavamenu li.backLava {
    margin-top: 1px !important;
    background: none;
    border: none;
    border-radius: 0px;
    border-bottom: 3px solid #0074c3;
}

#lavamenu ul {
    border: 0px;
    border-radius : 2px;
    top: 49px;
}
.message {
    -webkit-border-radius : 12px;
}

/*************** CONTENT ******************
****************************************
***************************************/
.wrapper {
 top: 120px !important;   
}

.content {
   background: #fff;
   border-radius: 2px;
   border: 1px solid #EDEDED;
   padding: 7px 19px;
}    

    .block>h2 span {
    padding:0px !important;
    border: 0px !important;
    border-radius: 0px !important;
    background: none;
    color: #333;
    font-size: 26px;
    line-height: 50px;
    font-weight: 300;                                                                                    
                    }

	.block>h2 {
    border: 0px !important;
    background: none !important;                                        
    box-shadow: none !important;                                    
  }

div.newsWrapper {
    border-radius: 5px;
    border: 1px solid #E7E7E7;
}
/* COPYRIGHT TOMAKLODXYZ */
.accordion h3.title {
    border: none;
 	padding: 7px 9px;
    border-bottom: dotted 1px #E7E7E7;
    background: #FBFBFB url(https://www.t411.ch/themes/blue/images/icon-arrow-square.png) no-repeat right -51px;
}
.accordion h3.title:hover {
    background: #F5F5F5;
}

.title {
    font-size:16px;
    font-weight:400;
    color: #333;
}

.accordion>div {
 	background :#FFFFFF;
    border: 0px;
}
    
/* COPYRIGHT TOMAKLODXYZ */

  
}
