css:
@-moz-document url-prefix("http://duelysthub.com/") {
/*@import url(https://fonts.googleapis.com/css?family=Montserrat:400,700);*/
@font-face {
  font-family: 'Montserrat';
  font-style: normal;
  font-weight: 400;
  src: local('Montserrat-Regular'), url(https://fonts.gstatic.com/s/montserrat/v6/zhcz-_WihjSQC0oHJ9TCYAzyDMXhdD8sAj6OAJTFsBI.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

/* -------------------------------------------------------------- */
/* GENERAL */
/* -------------------------------------------------------------- */
span,
.label,
input,
b,
p,
h1,
h2,
h3,
h4,
h5,
.title,
.date {
    font-family: "Montserrat" !important;
    letter-spacing: 0;
}

/* HIGHLIGHT COLOR */
a:focus,
a:hover,
.youplay-user .user-data .activity>div .num,
.youplay-matches-list .youplay-single-match:hover .youplay-match-title {
    color: #fe1 !important;
}

/* -------------------------------------------------------------- */
/* NAVIGATION */
/* -------------------------------------------------------------- */
.navbar-youplay {
    background: rgba(8, 3, 37, .6);
}
.navbar-youplay .navbar-nav>li>a {
    font-size: 1.8rem;
    line-height: 3.3rem;
    padding: 13px 0;
}

/* -------------------------------------------------------------- */
/* BANNER */
/* -------------------------------------------------------------- */
.youplay-banner .info .youplay-user h2 {
    font-size: 2.5rem;
    margin: 0 0 1.4rem .4rem;
    text-align: center;
}
.youplay-user .user-data .activity {
    text-align: center;
}
.youplay-user .user-data .location {
    display: none;
    font-size: 1rem;
    width: 100%;
    text-align: center;
}
.youplay-user .user-data {
    padding: 1.6rem 2.7rem;
    margin: 0 6px 6px 0px;
    height: auto;
}
.youplay-user .user-data:after {
    background: rgba(8, 3, 37, .9);
}
.youplay-banner div .container h1 {
    display: none;
}
.youplay-user .user-data .activity {
    margin-top: .7rem;
}
.youplay-user .user-data .activity>div .title {
    font-size: 1.1rem;
    margin-top: .3rem;
}
.youplay-banner,
.youplay-banner .small {
    min-height: 340px !important;
}
.youplay-banner.banner-top {
    padding-top: 0px;
}
.youplay-banner {
    background: url("https://cdn1.artstation.com/p/assets/images/images/000/215/865/large/anton-fadeev-obsidian-woods1-2-m-800.jpg") bottom !important;
}
@media (max-width: 767px) {
    .youplay-user {
        margin: 70px auto;
    }
    .youplay-banner .info .youplay-user h2 {
        font-size: 2rem;
        margin-bottom: .2rem;
        margin-top: 0;
    }
    .youplay-user .user-data .activity {
        margin-top: 0.3rem;
    }
    .youplay-user .user-data .activity>div .num {
        font-size: 2rem;
        line-height: 3rem;
    }
    .youplay-user .user-data {
        padding: 1.6rem 2.7rem;
        margin: 0 6px 6px 0px;
    }
}

/* -------------------------------------------------------------- */
/* CONTENT */
/* -------------------------------------------------------------- */
.youplay-user-navigation ul li>a:after {
    background: rgba(48, 43, 77, .9);
}
.content-wrap {
    max-width: none;
}
.bottom-info {
    text-transform: Capitalize;
}
.youplay-matches-list .youplay-single-match {
    padding-top: 0rem;
    padding-bottom: 0rem;
}
.content-wrap .youplay-content {
    margin: 4rem auto;
}
.youplay-matches-list .youplay-single-match .angled-img,
.youplay-matches-list .youplay-single-match .faction-img {
    max-width: 8rem;
}
}
