css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ex.ua") {
  body {background: #ecd2b7!important; background-color: #ecd2b7!important; background-blend-mode: normal!important; background-attachment: fixed!important; font-family:'Helvetica Neue', sans-serif!important; color: #221003!important;} 
  body > table {position: relative!important; max-width: 80%!important; margin: auto!important; background: #ecd2b7!important; box-shadow: 0 0 50px rgba(91,57,30,.33)} 
  font {color: #221003!important;} 
  .fox_col1, .fox_col2, #body_element > center > div {background: #ecd2b7!important;} 
  #body_element, .table tr {padding: 30px 40px 0 40px!important;}
  span {font-size: 10pt!important; color: #9a6a4a!important;	font-weight: normal!important;}
  select {border: 1px solid #5b391e!important; border-radius: 1px; box-shadow: 0 0 1px rgba(255,255,255,.3) inset, 0 0 1px rgba(0,0,0,.40)!important; margin: 0!important; background: #ecd2b7 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #5b391e!important;}
   #announce, #ad_block_1, #ad_block_2, #ex_block_1, #ex_block_2, div[id*="admaster_0"], div[id*="adriver_banner"], #search_help, #html-id, .list td.small p:nth-child(2n+2), span.small, #claim_button, div[id*="admixer_"], div[id*="mixer_"] {display: none!important;} 
  #search_help, .list td.small p:nth-child(2n+2), span.small, #claim_button, div[id*="admixer_"] {display: inline!important;}
  td {font-size: 12pt!important;} 
  td#body_element > center {margin: -16px -16px -16px!important;}
  div.gsAddInfo, div.gsLinks, td.col.h24, div.col_h.today, div.gsAddInfo3, div.gsTemp, div.gsCity, div.gsWeatherIcon, div.gsAddInfo2, td#colsDays {background: #ecd2b7!important;} 
  div#gsInformerID-ovY10eyK0RbLq2.gsInformer {border:none!important;}
  .menu {position: fixed!important; max-width: 100%!important; left:0!important; top:0!important; height:43px!important; padding-right: 10.5%!important; background: #ecd2b7!important; box-shadow: 0 4px 8px rgb(236,210,183) inset, 0 4px 5px rgba(115,105,92,.28); z-index: 1000!important; border:none!important;} 
  .menu a {font-size: 12pt!important;	color: #362212!important;	font-weight: normal!important;}  
  .menu a:hover {color: #a4750e!important;}
  .menu_text {color: transparent;}
  .menu_login, td.menu_text select {border: none!important; background: none!important; font-weight: normal; color: #5b391e!important;}   
  .include_0 td {background: #f0dcc7!important; padding: 8px!important; border: 1px solid #f1dfcc!important; border-radius: 6px; box-shadow: 0 0 8px rgba(36,22,12,.28);} 
  .include_0 td:active,.include_1 td:active, .panel td:active { position: relative; top: 2px;}
  .include_1 td, .panel td {padding: 10px 10px 2px 11px !important; border: 1px solid #f1dfcc!important; border-radius: 3px; box-shadow: 0 0 8px rgba(36,22,12,.22);}  
  .include_0 td:hover, .include_1 td:hover, .panel td:hover {box-shadow: 0 1px 12px rgba(36,22,12,.26);}
  .include_0 td > a, .include_0 td > small {position: relative!important; top: 3px!important;} 
  .include_0 div, .panel .r_button_small {position: relative!important; bottom: 3px!important;}
  .include_1 div {position: relative!important; bottom: 8px!important;}
  .panel p, .include_0 p {margin: 3px 0!important;} 
  .include_0 img,.include_1 img,.panel img {filter: drop-shadow(0 0 8px rgba(36,22,12,.60)) contrast(125%); -webkit-filter: drop-shadow(0 0 8px rgba(36,22,12,.60)) contrast(125%);}
  .include_0 img:hover,.include_1 img:hover,.panel img:hover {filter: drop-shadow(0 0 10px rgba(36,22,12,.80)) contrast(125%); -webkit-filter: drop-shadow(0 0 10px rgba(36,22,12,.80)) contrast(125%);}
  #search_box, #search_form, #search_link {position: inherit!important;} 
  #body_element > div:nth-child(4) {padding: 0!important;}
  #search_form > div:nth-child(2) {max-height: 0!important; max-width: 0!important; padding: 0!important;}
  #search_form > div:nth-child(2) > div:nth-child(1) {left: 4px!important; height: 0!important;} 
  #search_form > div > div:nth-child(1) {right: 11px!important; bottom: 16px}
  div:nth-child(4) form {position: relative!important; top: 8px!important; left: 9px!important;}
  form:nth-child(5) {position: inherit!important;}
  #body_element>p:nth-child(3), div:nth-child(4)>p {margin-top: 20px!important; margin-bottom: 5px!important;}
  #search_text {width: 400px!important; height: 31px!important; box-sizing: border-box!important; border-radius: 3px!important; font-weight: bold!important; font-size: 16px!important; box-shadow: 0 1px 8px rgba(0,0,0,.24)}
  #search_line {width: 400px!important; top: 7px!important;}
  #search_hint {box-shadow: 0 1px 10px rgba(0,0,0,.45); font-weight: bold!important;	font-size: 12pt!important;} 
  .search_hint_div_active {background-color: #a6856a!important;}
  #search_button {top: 7px!important; left: 405px!important; height: 31px!important; width: 140px!important; font-size: 16px!important; color: #f8e4c4!important; font-weight: bold!important;} 
  #search_button:hover {color: #5b391e!important;} 
  div:nth-child(4) > p, #body_element > p:nth-child(3) {padding: 0 0 0 580px!important; color: #5b391e!important;} 
  div.news-bottom-note {background: #ecd2b7!important; color: #9b6417!important;}
  div#search_hint {background: #ecd2b7!important; border: 1px solid #5b391e!important; border-radius: 2px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important;}
  div.mlist {background: #ecd2b7!important; border: 1px solid #5b391e!important; border-radius: 4px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 3px 9px rgba(0,0,0,.55)!important;} 
  #news div.mlist span {border-bottom: 1px dotted #5b391e;}
  div.fox_body {background: #ecd2b7!important; border: 1px solid #5b391e; border-radius: 1px; margin: 0!important;}
  div.button {background: url("http://static.ex.ua/morphine_suffering/button.png")!important;} 
  div.button:hover {background: url(http://static.ex.ua/morphine_suffering/button_hover.png)!important;} 
  button#fox_create.fox_btn {border: 1px solid #5b391e!important; border-radius: 2px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #5b391e linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #f8e4c4!important;}
  button#fox_create.fox_btn:hover {color: black!important; background: #ecd2b7!important;}
  button#fox_exp_send.fox_btn{border: 1px solid #5b391e!important; border-radius: 4px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #5b391e linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #f8e4c4!important;}
  button#fox_exp_send.fox_btn:hover {color: black!important; background: #ecd2b7!important;}
  li.country {background: #f8e4c4!important;}
  .list {max-width: 100%!important; font-style: italic!important;}
  .list td, .comment td {padding: 0!important; border-bottom: 1px dashed #9c8878!important; color: #5b391e!important;}
  .list tr td {background : #ecd2b7!important;}
  .list tr th {background : #ecd2b7!important; border-bottom: 1px dotted #5b391e!important; border-top: 1px dotted #5b391e!important;}
  #body_element>p:nth-child(10) {display: inline-block!important; position: relative!important; top: 4px!important; margin: 0!important;}
  #body_element a:nth-child(9),.list td.small b {padding: 0 10px 0 0!important;}  
  .vjs-poster {z-index: 1!important;}  
  a {color: #5b391e!important;} a:hover {color: #9b6417!important;} 
  b {color: #5b391e!important;} 
  a > b:hover {color: #a4750e!important;} 
  .include_0 b,.list b {font-size: 14px!important;} small {font-size: 12px!important;}
  h1 {color: #5b391e!important;	font-size: 14pt!important; font-weight: 600!important;} 
  h2:hover {color: #9b6417!important;}
  h2, #body_element > table:nth-child(9) h1 {color: #5b391e!important; font-size: 22pt!important; font-weight: 700!important;}  
  a.info {font-size: 13px!important; border: none!important;}
  .button, .r_button a {border: 1px solid #5b391e!important; border-radius: 2px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #5b391e linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #f8e4c4!important;}
  .button:hover, .r_button a:hover {color: #5b391e!important; background: #ecd2b7!important;}
  .small_button, .list .r_button_small a {border: 1px solid #5b391e!important; border-radius: 2px!important; box-shadow: 0 1px 2px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.5)!important; background: #5b391e linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.03))!important; font-weight: 500!important; color: #f8e4c4!important;}
  .small_button:hover, .r_button_small a:hover {color: #5b391e!important; background: #ecd2b7!important;}
  .panel .r_button_small a, div.r_button_small a, .comment .r_button_small a {background: #ecd2b7; border: 1px solid #5b391e; border-radius: 2px; margin: 0!important;}
  .panel .r_button_small a:hover, div.r_button_small a:hover, .comment .r_button_small a:hover {background: #f8e4c4!important; border: 1px solid #5b391e; border-radius: 2px; margin: 0!important;}
  input {background: #ecd2b7!important; color: #51331b!important; font-family: sans-serif!important; font-size: 14px!important; font-weight: 600!important; border: 1px solid #5b391e!important; padding-left: 10px;}
  .intl-tel-input .country-list .country {background: #ecd2b7!important;}
  .intl-tel-input .country-list {border: 1px solid #5b391e!important; border-radius: 4px!important;}
  textarea {background: #ecd2b7!important; color: #5b391e!important; border: 1px solid #5b391e!important; font-family: 'Helvetica Neue', sans-serif!important; font-size: 12pt!important; padding: 0 4px!important;}
  tbody > tr > td > table > tbody > tr > td {padding-top: 4px!important;}
  #body_element > table > tbody > tr > td > table > tbody > tr > td:nth-child(2){min-width:610px!important;}
  img {filter: drop-shadow(0 0 4px rgba(72,45,24,.45)) contrast(125%); -webkit-filter: drop-shadow(0 0 4px rgba(72,45,24,.45)) contrast(125%);}
  img#viewer_img {filter: none; -webkit-filter: none;}
  ul.country-list { background: #ecd2b7!important;}
  .ex_title {color: #5b391e!important; font-size: 18pt!important;} 
  .fox_desc {color: #5b391e!important;} 
  .fox_h {color: #5b391e!important;} 
  .modify_time {color: #81461d!important;} 
  small {color: #6d4122!important;}
  #news .top li span {border-bottom: 1px solid #5b391e; font-size: 18px!important; color: #7b604a!important;}
  #news .watch {border:none!important;}
  #news #ti li {border-bottom: 1px dashed #9c8878!important;} 
  #news div.more span {border-bottom: 1px solid #a65326!important;}
  .news {color: #5b391e!important; font-size: 24px!important;} 
  .link_body .top_right div {border-bottom: 1px dotted #5b391e; margin-left: 15px; padding: 5px 0;}
  .link_body .top_right span {border-bottom: 1px solid #5b391e; font-size: 20px!important; color: #5b391e!important;}
  [contentEditable="true"]:empty:not(:focus)::before { color: #8c7461!important;} 
   element.style {position: relative; z-index: 1;} #uploadButton {border: 1px solid #5b391e!important; border-radius: 1px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #5b391e linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #f8e4c4!important; font-size: 12pt !important;} 
  #uploadButton:hover {color: black!important; background: #ecd2b7!important;} 
  .small {color: #5b391e!important;} 
  .ex_desc {color: #7b604a!important;}
  #news #ti .of p {color: #7b604a!important;} 
  #content_page {margin: 65px!important; font-size: 12pt!important; color: #5b391e!important;} 
  #usrpg .wr {margin-top: -15px!important;} 
  #usrpg .pg_menu a b {color: #dac79e!important;} 
  #usrpg .pg_menu a b:hover {color: #a4750e!important;} 
  .gsInformer .cols .col_h.today, .gsInformer .cols .col_h.tomorrow {color: #5b391e!important;} 
  .gsInformer .gsAddInfo {color: #5b391e!important;} 
  .pos_up {color: #266d18!important;} 
  .pos_down {color: #a0241c!important;} 
  td.menu_text:nth-child(1){text-align: center!important; padding-left: 55px!important;} 
}
