css:
@-moz-document domain("www.presse-citron.net") {

body, #mvp-site, #mvp-site-wall, #mvp-main-body-wrap {
    background-color: #222 !important;
    background: #222 !important;
    color: #999 !important;
}
h1, h2, h3, h4, h5, h6, h1 > p, h2 > p, h3 > p, h4 > p, h5 > p, h6 > p, .post-content h1, .post-content h2, .post-content h3, .post-content h4, .post-content h5, .post-content h6, .highlights h2 a, .block.posts a, .highlights .section-head a, .gallery-block .carousel .title a {
    background-color: #222 !important;
    background: #222 !important;
    color: #888 !important;
    text-align: justify !important;
}
p {
    color: #999 !important;
}
a, #mvp-content-main p a, .mvp-post-add-main p a {
    color: #00c853 !important;
}
form#commentform {
    padding: 10px !important;
}
#review-statistics .review-wrap-up, .affiliate-button, .comp-center {
    display: none !important;
}

}
